import React, { useState } from 'react';
import './OrderTracking.css';

interface VehicleOrder {
  id: string;
  orderNumber: string;
  orderDate: string;
  customerName: string;
  customerContact: string;
  customerNIC: string;
  vehicleDetails: {
    brand: string;
    model: string;
    year: number;
    color: string;
    specifications?: string;
  };
  pricing: {
    vehiclePrice: number;
    taxes: number;
    fees: number;
    totalAmount: number;
  };
  advancePayment: number;
  balanceAmount: number;
  orderStatus: 'pending' | 'confirmed' | 'in_transit' | 'arrived' | 'delivered' | 'cancelled';
  expectedArrivalDate?: string;
  actualArrivalDate?: string;
  deliveryDate?: string;
  notes?: string;
  timeline: {
    date: string;
    status: string;
    description: string;
  }[];
}

interface OrderTrackingProps {
  onMoveToInventory: (order: VehicleOrder) => void;
}

const OrderTracking: React.FC<OrderTrackingProps> = ({ onMoveToInventory }) => {
  const [orders, setOrders] = useState<VehicleOrder[]>([
    {
      id: 'ORD001',
      orderNumber: 'ORD-2024-001',
      orderDate: '2024-10-01',
      customerName: 'Ahmed Al-Rashid',
      customerContact: '+971-50-123-4567',
      customerNIC: '784-1985-1234567-1',
      vehicleDetails: {
        brand: 'Toyota',
        model: 'Land Cruiser',
        year: 2024,
        color: 'Pearl White',
        specifications: 'V6, Full Option, Sunroof'
      },
      pricing: {
        vehiclePrice: 250000,
        taxes: 12500,
        fees: 2500,
        totalAmount: 265000
      },
      advancePayment: 50000,
      balanceAmount: 215000,
      orderStatus: 'in_transit',
      expectedArrivalDate: '2024-11-15',
      timeline: [
        { date: '2024-10-01', status: 'Order Placed', description: 'Customer placed order with advance payment' },
        { date: '2024-10-05', status: 'Order Confirmed', description: 'Order confirmed by supplier' },
        { date: '2024-10-10', status: 'In Transit', description: 'Vehicle shipped from Japan' }
      ]
    },
    {
      id: 'ORD002',
      orderNumber: 'ORD-2024-002',
      orderDate: '2024-10-10',
      customerName: 'Sarah Johnson',
      customerContact: '+971-55-987-6543',
      customerNIC: '784-1990-7654321-2',
      vehicleDetails: {
        brand: 'BMW',
        model: 'X7',
        year: 2024,
        color: 'Black Sapphire',
        specifications: 'M Sport, 7 Seater, Premium Package'
      },
      pricing: {
        vehiclePrice: 350000,
        taxes: 17500,
        fees: 3000,
        totalAmount: 370500
      },
      advancePayment: 100000,
      balanceAmount: 270500,
      orderStatus: 'confirmed',
      expectedArrivalDate: '2024-12-01',
      timeline: [
        { date: '2024-10-10', status: 'Order Placed', description: 'Customer placed order with advance payment' },
        { date: '2024-10-12', status: 'Order Confirmed', description: 'Order confirmed by supplier' }
      ]
    }
  ]);

  const [showOrderForm, setShowOrderForm] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState<VehicleOrder | null>(null);
  const [editingOrder, setEditingOrder] = useState<VehicleOrder | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-LK', {
      style: 'currency',
      currency: 'LKR',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      pending: '#ffc107',
      confirmed: '#2196f3',
      in_transit: '#9c27b0',
      arrived: '#4caf50',
      delivered: '#4caf50',
      cancelled: '#f44336'
    };
    return colors[status] || '#757575';
  };

  const getStatusIcon = (status: string) => {
    const icons: Record<string, string> = {
      pending: 'â³',
      confirmed: 'âœ…',
      in_transit: 'ðŸš¢',
      arrived: 'ðŸ“¦',
      delivered: 'ðŸš—',
      cancelled: 'âŒ'
    };
    return icons[status] || 'ðŸ“‹';
  };

  const handleMoveToInventory = (order: VehicleOrder) => {
    if (order.orderStatus !== 'arrived') {
      alert('Vehicle must be in "Arrived" status before moving to inventory!');
      return;
    }
    
    if (window.confirm(`Move ${order.vehicleDetails.brand} ${order.vehicleDetails.model} to inventory?`)) {
      onMoveToInventory(order);
      // Update order status to delivered
      setOrders(orders.map(o => 
        o.id === order.id 
          ? { ...o, orderStatus: 'delivered' as const, deliveryDate: new Date().toISOString().split('T')[0] }
          : o
      ));
      alert('Vehicle moved to inventory successfully!');
    }
  };

  const handleUpdateStatus = (orderId: string, newStatus: VehicleOrder['orderStatus']) => {
    setOrders(orders.map(order => {
      if (order.id === orderId) {
        const newTimeline = [...order.timeline, {
          date: new Date().toISOString().split('T')[0],
          status: newStatus.replace('_', ' ').toUpperCase(),
          description: `Status updated to ${newStatus}`
        }];
        
        return {
          ...order,
          orderStatus: newStatus,
          timeline: newTimeline,
          actualArrivalDate: newStatus === 'arrived' ? new Date().toISOString().split('T')[0] : order.actualArrivalDate
        };
      }
      return order;
    }));
  };

  const filteredOrders = orders.filter(order => {
    const matchesSearch = 
      order.orderNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.vehicleDetails.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.vehicleDetails.model.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesStatus = statusFilter === 'all' || order.orderStatus === statusFilter;
    
    return matchesSearch && matchesStatus;
  });

  // Calculate statistics
  const stats = {
    total: orders.length,
    pending: orders.filter(o => o.orderStatus === 'pending').length,
    confirmed: orders.filter(o => o.orderStatus === 'confirmed').length,
    inTransit: orders.filter(o => o.orderStatus === 'in_transit').length,
    arrived: orders.filter(o => o.orderStatus === 'arrived').length,
    totalAdvance: orders.reduce((sum, o) => sum + o.advancePayment, 0),
    totalValue: orders.filter(o => o.orderStatus !== 'delivered' && o.orderStatus !== 'cancelled').reduce((sum, o) => sum + o.pricing.totalAmount, 0)
  };

  return (
    <div className="order-tracking-container">
      {/* Header */}
      <div className="tracking-header">
        <div>
          <h2>ðŸ“‹ Customer Bookings & Pre-Orders</h2>
          <p className="subtitle">Track customer vehicle bookings from order to delivery</p>
        </div>
        <button className="btn btn-primary" onClick={() => setShowOrderForm(true)}>
          âž• New Booking
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon" style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>ðŸ“¦</div>
          <div className="stat-content">
            <h3>{stats.total}</h3>
            <p>Total Orders</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)'}}>ðŸš¢</div>
          <div className="stat-content">
            <h3>{stats.inTransit}</h3>
            <p>In Transit</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'}}>âœ…</div>
          <div className="stat-content">
            <h3>{stats.arrived}</h3>
            <p>Arrived</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{background: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)'}}>ðŸ’°</div>
          <div className="stat-content">
            <h3>{formatCurrency(stats.totalAdvance)}</h3>
            <p>Total Advances</p>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="tracking-filters">
        <input
          type="text"
          className="search-input"
          placeholder="ðŸ” Search by order number, customer, or vehicle..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        <select
          className="filter-select"
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
        >
          <option value="all">All Status</option>
          <option value="pending">Pending</option>
          <option value="confirmed">Confirmed</option>
          <option value="in_transit">In Transit</option>
          <option value="arrived">Arrived</option>
          <option value="delivered">Delivered</option>
          <option value="cancelled">Cancelled</option>
        </select>
      </div>

      {/* Orders List */}
      <div className="orders-grid">
        {filteredOrders.map(order => (
          <div key={order.id} className="order-card">
            <div className="order-card-header">
              <div>
                <h3>{order.vehicleDetails.brand} {order.vehicleDetails.model}</h3>
                <p className="order-number">{order.orderNumber}</p>
              </div>
              <div 
                className="status-badge"
                style={{background: getStatusColor(order.orderStatus)}}
              >
                {getStatusIcon(order.orderStatus)} {order.orderStatus.replace('_', ' ').toUpperCase()}
              </div>
            </div>

            <div className="order-details">
              <div className="detail-row">
                <span className="label">ðŸ‘¤ Customer:</span>
                <span className="value">{order.customerName}</span>
              </div>
              <div className="detail-row">
                <span className="label">ðŸ“ž Contact:</span>
                <span className="value">{order.customerContact}</span>
              </div>
              <div className="detail-row">
                <span className="label">ðŸŽ¨ Color:</span>
                <span className="value">{order.vehicleDetails.color}</span>
              </div>
              <div className="detail-row">
                <span className="label">ðŸ“… Order Date:</span>
                <span className="value">{new Date(order.orderDate).toLocaleDateString()}</span>
              </div>
              {order.expectedArrivalDate && (
                <div className="detail-row">
                  <span className="label">ðŸš¢ Expected:</span>
                  <span className="value">{new Date(order.expectedArrivalDate).toLocaleDateString()}</span>
                </div>
              )}
            </div>

            <div className="order-pricing">
              <div className="pricing-row">
                <span>Total Amount:</span>
                <strong>{formatCurrency(order.pricing.totalAmount)}</strong>
              </div>
              <div className="pricing-row advance">
                <span>Advance Paid:</span>
                <strong>{formatCurrency(order.advancePayment)}</strong>
              </div>
              <div className="pricing-row balance">
                <span>Balance:</span>
                <strong>{formatCurrency(order.balanceAmount)}</strong>
              </div>
            </div>

            <div className="order-actions">
              <button 
                className="btn-action btn-view"
                onClick={() => setSelectedOrder(order)}
              >
                ðŸ‘ï¸ View Details
              </button>
              
              {order.orderStatus === 'arrived' && (
                <button 
                  className="btn-action btn-inventory"
                  onClick={() => handleMoveToInventory(order)}
                >
                  ðŸ“¦ Move to Inventory
                </button>
              )}

              {order.orderStatus !== 'delivered' && order.orderStatus !== 'cancelled' && (
                <select
                  className="status-update-select"
                  value={order.orderStatus}
                  onChange={(e) => handleUpdateStatus(order.id, e.target.value as VehicleOrder['orderStatus'])}
                >
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="in_transit">In Transit</option>
                  <option value="arrived">Arrived</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              )}
            </div>
          </div>
        ))}
      </div>

      {filteredOrders.length === 0 && (
        <div className="no-orders">
          <p>ðŸ“­ No orders found</p>
        </div>
      )}

      {/* New Booking Form Modal */}
      {showOrderForm && (
        <div className="modal-overlay" onClick={() => setShowOrderForm(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>âž• New Customer Booking</h2>
              <button className="close-btn" onClick={() => setShowOrderForm(false)}>Ã—</button>
            </div>

            <div className="modal-body">
              <form onSubmit={(e) => {
                e.preventDefault();
                const formData = new FormData(e.currentTarget);
                const newOrder: VehicleOrder = {
                  id: `ORD${(orders.length + 1).toString().padStart(3, '0')}`,
                  orderNumber: `ORD-2024-${(orders.length + 1).toString().padStart(3, '0')}`,
                  orderDate: new Date().toISOString().split('T')[0],
                  customerName: formData.get('customerName') as string,
                  customerContact: formData.get('customerContact') as string,
                  customerNIC: formData.get('customerNIC') as string,
                  vehicleDetails: {
                    brand: formData.get('brand') as string,
                    model: formData.get('model') as string,
                    year: parseInt(formData.get('year') as string),
                    color: formData.get('color') as string,
                    specifications: formData.get('specifications') as string
                  },
                  pricing: {
                    vehiclePrice: parseFloat(formData.get('vehiclePrice') as string),
                    taxes: parseFloat(formData.get('taxes') as string) || 0,
                    fees: parseFloat(formData.get('fees') as string) || 0,
                    totalAmount: 0
                  },
                  advancePayment: parseFloat(formData.get('advancePayment') as string),
                  balanceAmount: 0,
                  orderStatus: 'pending',
                  expectedArrivalDate: formData.get('expectedArrivalDate') as string,
                  timeline: [{
                    date: new Date().toISOString().split('T')[0],
                    status: 'Order Placed',
                    description: 'Customer placed booking with advance payment'
                  }]
                };
                
                // Calculate totals
                newOrder.pricing.totalAmount = newOrder.pricing.vehiclePrice + newOrder.pricing.taxes + newOrder.pricing.fees;
                newOrder.balanceAmount = newOrder.pricing.totalAmount - newOrder.advancePayment;
                
                setOrders([newOrder, ...orders]);
                setShowOrderForm(false);
                alert('Booking created successfully!');
              }}>
                <div className="details-section">
                  <h3>Customer Information</h3>
                  <div className="info-grid">
                    <div className="info-item">
                      <label>Customer Name *</label>
                      <input type="text" name="customerName" className="form-input" required />
                    </div>
                    <div className="info-item">
                      <label>Contact Number *</label>
                      <input type="tel" name="customerContact" className="form-input" required />
                    </div>
                    <div className="info-item full-width">
                      <label>NIC Number *</label>
                      <input type="text" name="customerNIC" className="form-input" required />
                    </div>
                  </div>
                </div>

                <div className="details-section">
                  <h3>Vehicle Details</h3>
                  <div className="info-grid">
                    <div className="info-item">
                      <label>Brand *</label>
                      <input type="text" name="brand" className="form-input" required />
                    </div>
                    <div className="info-item">
                      <label>Model *</label>
                      <input type="text" name="model" className="form-input" required />
                    </div>
                    <div className="info-item">
                      <label>Year *</label>
                      <input type="number" name="year" className="form-input" min="2000" max="2030" required />
                    </div>
                    <div className="info-item">
                      <label>Color *</label>
                      <input type="text" name="color" className="form-input" required />
                    </div>
                    <div className="info-item full-width">
                      <label>Specifications</label>
                      <textarea name="specifications" className="form-input" rows={2} placeholder="e.g., Full Option, Sunroof, Leather Seats"></textarea>
                    </div>
                  </div>
                </div>

                <div className="details-section">
                  <h3>Pricing & Payment</h3>
                  <div className="info-grid">
                    <div className="info-item">
                      <label>Vehicle Price (LKR) *</label>
                      <input type="number" name="vehiclePrice" className="form-input" step="0.01" required placeholder="e.g., 5000000" />
                    </div>
                    <div className="info-item">
                      <label>Taxes (LKR) <span style={{color: '#7f8c8d', fontSize: '12px'}}>(Optional)</span></label>
                      <input type="number" name="taxes" className="form-input" step="0.01" defaultValue="0" placeholder="0" />
                    </div>
                    <div className="info-item">
                      <label>Fees (LKR) <span style={{color: '#7f8c8d', fontSize: '12px'}}>(Optional)</span></label>
                      <input type="number" name="fees" className="form-input" step="0.01" defaultValue="0" placeholder="0" />
                    </div>
                    <div className="info-item">
                      <label>Advance Payment (LKR) *</label>
                      <input type="number" name="advancePayment" className="form-input" step="0.01" required placeholder="e.g., 500000" />
                    </div>
                    <div className="info-item full-width">
                      <label>Expected Arrival Date <span style={{color: '#7f8c8d', fontSize: '12px'}}>(Optional)</span></label>
                      <input type="date" name="expectedArrivalDate" className="form-input" />
                    </div>
                  </div>
                </div>

                <div style={{display: 'flex', gap: '10px', justifyContent: 'flex-end', marginTop: '20px'}}>
                  <button type="button" className="btn btn-secondary" onClick={() => setShowOrderForm(false)}>
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Create Booking
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Order Details Modal */}
      {selectedOrder && (
        <div className="modal-overlay" onClick={() => setSelectedOrder(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>ðŸ“‹ Order Details</h2>
              <button className="close-btn" onClick={() => setSelectedOrder(null)}>Ã—</button>
            </div>

            <div className="modal-body">
              <div className="details-section">
                <h3>Vehicle Information</h3>
                <div className="info-grid">
                  <div className="info-item">
                    <label>Brand:</label>
                    <span>{selectedOrder.vehicleDetails.brand}</span>
                  </div>
                  <div className="info-item">
                    <label>Model:</label>
                    <span>{selectedOrder.vehicleDetails.model}</span>
                  </div>
                  <div className="info-item">
                    <label>Year:</label>
                    <span>{selectedOrder.vehicleDetails.year}</span>
                  </div>
                  <div className="info-item">
                    <label>Color:</label>
                    <span>{selectedOrder.vehicleDetails.color}</span>
                  </div>
                  {selectedOrder.vehicleDetails.specifications && (
                    <div className="info-item full-width">
                      <label>Specifications:</label>
                      <span>{selectedOrder.vehicleDetails.specifications}</span>
                    </div>
                  )}
                </div>
              </div>

              <div className="details-section">
                <h3>Customer Information</h3>
                <div className="info-grid">
                  <div className="info-item">
                    <label>Name:</label>
                    <span>{selectedOrder.customerName}</span>
                  </div>
                  <div className="info-item">
                    <label>Contact:</label>
                    <span>{selectedOrder.customerContact}</span>
                  </div>
                  <div className="info-item full-width">
                    <label>NIC:</label>
                    <span>{selectedOrder.customerNIC}</span>
                  </div>
                </div>
              </div>

              <div className="details-section">
                <h3>Order Timeline</h3>
                <div className="timeline">
                  {selectedOrder.timeline.map((event, index) => (
                    <div key={index} className="timeline-item">
                      <div className="timeline-dot"></div>
                      <div className="timeline-content">
                        <div className="timeline-date">{new Date(event.date).toLocaleDateString()}</div>
                        <div className="timeline-status">{event.status}</div>
                        <div className="timeline-desc">{event.description}</div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrderTracking;
