  const extractBrandFromModel = (model: string): string => {
    const brandMap: { [key: string]: string } = {
      'toyota': 'Toyota',
      'camry': 'Toyota',
      'corolla': 'Toyota',
      'prius': 'Toyota',
      'honda': 'Honda',
      'accord': 'Honda',
      'civic': 'Honda',
      'cr-v': 'Honda',
      'bmw': 'BMW',
      'x5': 'BMW',
      'x3': 'BMW',
      '3 series': 'BMW',
      'nissan': 'Nissan',
      'gtr': 'Nissan',
      'altima': 'Nissan',
      'tesla': 'Tesla',
      'model s': 'Tesla',
      'model 3': 'Tesla',
      'jeep': 'Jeep',
      'wrangler': 'Jeep',
      'mercedes': 'Mercedes',
      'audi': 'Audi'
    };

    const modelLower = model.toLowerCase();
    for (const [key, brand] of Object.entries(brandMap)) {
      if (modelLower.includes(key)) {
        return brand;
      }
    }
    return model.split(' ')[0]; // Fallback to first word
  };

