                </select>
              </div>
            </div>

            {/* Customer Information */}
            <div className="form-section">
              <h3>üë§ Customer Information</h3>
              <div className="form-grid">
                <div className="form-group">
                  <label className="form-label">Customer Name *</label>
                  <input
                    type="text"
                    value={customerData.name}
