            <p style={{color: '#666', marginBottom: '1rem'}}>
              Select a transaction to process payment:
            </p>

            <div className="transaction-list">
              {transactions.map(transaction => (
                <div 
                  key={transaction.id} 
                  className="transaction-item"
                  onClick={() => onSelectTransaction(transaction)}
