import React, { useState } from 'react';
import './Dashboard.css';
import VehicleOrderForm from './VehicleOrderForm';
import Billing from './Billing';
import BankInvoice from './BankInvoice';
import CustomerInvoice from './CustomerInvoice';

interface VehicleOrder {
  id: string;
  model: string;
  year: number;
  country: string;
  orderDate: string;
  status: 'ordered' | 'shipped' | 'clearing' | 'completed';
  totalCost: number;
  expenses: {
    vehicleCost: number;
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    customExpenses: { [key: string]: number };
  };
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  profit?: number;
  sellingPrice?: number;
  supplier?: string;
  expectedDelivery?: string;
  paymentMethod?: string;
  notes?: string;
  // Advanced fields
  customerInfo?: {
    name: string;
    contact: string;
    paymentMethod: string;
  };
  profitMargin?: number;
  saleDate?: string;
  marketValue?: number;
  depreciation?: number;
  // LC and Additional fields
  vehicleNumber?: string;
  lcAmount?: number;
  lcBank?: string;
  additionalInfo?: string;
  // Basic Information fields
  vinNumber?: string;
  licensePlateNumber?: string;
  customBasicInfo?: { [key: string]: string };
}

interface InventoryItem {
  id: string;
  model: string;
  brand: string;
  year: number;
  color: string;
  mileage: number;
  condition: 'excellent' | 'good' | 'fair' | 'needs_repair';
  purchasePrice: number;
  marketValue: number;
  sellingPrice?: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  location: string;
  status: 'available' | 'reserved' | 'sold' | 'maintenance';
  dateAdded: string;
  lastUpdated: string;
  features: string[];
  images?: string[];
  vin?: string;
  licensePlate?: string;
  registrationNo?: string;
  fuelType: 'gasoline' | 'diesel' | 'hybrid' | 'electric';
  transmission: 'manual' | 'automatic' | 'cvt';
  engineSize?: string;
  bodyType: 'sedan' | 'suv' | 'hatchback' | 'coupe' | 'pickup' | 'van' | 'other';
  supplier?: string;
  purchaseDate?: string;
  warrantyExpiry?: string;
  notes?: string;
  reservedBy?: {
    name: string;
    contact: string;
    reservationDate: string;
    depositAmount?: number;
  };
  soldInfo?: {
    customerName: string;
    customerContact: string;
    saleDate: string;
    finalPrice: number;
    paymentMethod: string;
  };
}

interface InventoryFormData {
  model: string;
  brand: string;
  year: number;
  color: string;
  mileage: number;
  condition: 'excellent' | 'good' | 'fair' | 'needs_repair';
  purchasePrice: number;
  marketValue: number;
  sellingPrice?: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  location: string;
  features: string[];
  vin?: string;
  licensePlate?: string;
  fuelType: 'gasoline' | 'diesel' | 'hybrid' | 'electric';
  transmission: 'manual' | 'automatic' | 'cvt';
  engineSize?: string;
  bodyType: 'sedan' | 'suv' | 'hatchback' | 'coupe' | 'pickup' | 'van' | 'other';
  supplier?: string;
  purchaseDate?: string;
  warrantyExpiry?: string;
  notes?: string;
}

// Billing & POS Interfaces
interface Customer {
  id: string;
  name: string;
  contact: string;
  email?: string;
  address?: string;
  idNumber?: string;
  notes?: string;
  nic?: string;
  registrationDate?: string;
}

interface LeasingCompany {
  id: string;
  name: string;
  contactPerson?: string;
  phone?: string;
  email?: string;
  address?: string;
}

interface LeasingDetails {
  leasingCompanyId: string;
  leasingCompanyName: string;
  leaseReferenceNo: string;
  downPayment: number;
  leasingAmount: number;
  monthlyInstallment: number;
  tenure: number; // in months
  startDate: string;
  endDate: string;
  interestRate?: number;
}

interface PaymentRecord {
  id: string;
  transactionId: string;
  amount: number;
  paymentMethod: 'cash' | 'card' | 'bank_transfer' | 'check' | 'finance' | 'leasing';
  paymentDate: string;
  receivedBy: string;
  notes?: string;
  // Leasing specific fields
  leasingDetails?: LeasingDetails;
}

interface Transaction {
  id: string;
  type: 'reservation' | 'sale' | 'refund' | 'leasing';
  customerId: string;
  inventoryId: string;
  vehicleDetails: {
    brand: string;
    model: string;
    year: number;
    color: string;
    registrationNo?: string;
  };
  pricing: {
    vehiclePrice: number;
    taxes: number;
    fees: number;
    discount: number;
    totalAmount: number;
  };
  payments: PaymentRecord[];
  totalPaid: number;
  balanceRemaining: number;
  status: 'pending' | 'partial_paid' | 'fully_paid' | 'overdue' | 'cancelled' | 'completed';
  reservationDate?: string;
  expectedDelivery?: string;
  completionDate?: string;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  notes?: string;
  invoiceNumber?: string;
  // Leasing specific fields
  paymentMode?: 'cash' | 'bank_transfer' | 'leasing';
  isLeasing?: boolean;
  leasingDetails?: LeasingDetails;
}

interface Invoice {
  id: string;
  invoiceNumber: string;
  transactionId: string;
  customerId: string;
  issueDate: string;
  dueDate: string;
  items: {
    description: string;
    quantity: number;
    unitPrice: number;
    total: number;
  }[];
  subtotal: number;
  taxRate: number;
  taxAmount: number;
  totalAmount: number;
  status: 'draft' | 'sent' | 'paid' | 'overdue' | 'cancelled';
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  notes?: string;
}

interface VehicleOrderFormData {
  model: string;
  year: number;
  country: string;
  supplier: string;
  vehicleCost: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  expectedDelivery: string;
  notes: string;
  expenses: {
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    customExpenses: { [key: string]: number };
  };
  paymentMethod: string;
  // New fields
  vehicleNumber: string;
  lcAmount: number;
  lcBank: string;
  additionalInfo: string;
  // Basic Information fields
  vinNumber: string;
  licensePlateNumber: string;
  customBasicInfo: { [key: string]: string };
}

interface DashboardProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ activeTab, onTabChange }) => {
  const [showOrderForm, setShowOrderForm] = useState(false);
