import React, { useState, useEffect } from 'react';
import './OrderTracking.css';
import { vehicleOrderAPI, VehicleOrder as APIVehicleOrder } from '../utils/api';

interface VehicleOrder {
  _id?: string;
  id?: string;
  orderNumber: string;
  orderDate: string;
  customerName: string;
  customerContact: string;
  customerNIC?: string;
  vehicleDetails: {
    brand: string;
    model: string;
    year: number;
    color: string;
    specifications?: string;
  };
  pricing: {
    vehiclePrice: number;
    taxes: number;
    fees: number;
    totalAmount: number;
  };
  advancePayment: number;
  balanceAmount: number;
  orderStatus: 'pending' | 'confirmed' | 'in_transit' | 'arrived' | 'delivered' | 'cancelled';
  expectedArrivalDate?: string;
  actualArrivalDate?: string;
  deliveryDate?: string;
  notes?: string;
  timeline: {
    date: string;
    status: string;
    description: string;
  }[];
}

interface OrderTrackingProps {
  onMoveToInventory: (order: VehicleOrder) => void;
}

const OrderTracking: React.FC<OrderTrackingProps> = ({ onMoveToInventory }) => {
  const [orders, setOrders] = useState<VehicleOrder[]>([]);
  const [isLoadingOrders, setIsLoadingOrders] = useState(true);
  const [loadError, setLoadError] = useState<string | null>(null);

  const [showOrderForm, setShowOrderForm] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState<VehicleOrder | null>(null);
  const [editingOrder, setEditingOrder] = useState<VehicleOrder | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');

  // Fetch orders on component mount
  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      setIsLoadingOrders(true);
      setLoadError(null);

      const token = localStorage.getItem('authToken');
      if (!token) {
        throw new Error('No authentication token found. Please log in again.');
      }

      const response = await vehicleOrderAPI.getAll({ limit: 1000 });
      console.log('Loaded orders:', response.orders.length, 'orders');
      setOrders(response.orders);
    } catch (error: any) {
      console.error('Error loading orders:', error);
      setLoadError(error.message || 'Failed to load orders');
    } finally {
      setIsLoadingOrders(false);
    }
  };

  const handleSaveNewOrder = async (orderData: Omit<VehicleOrder, '_id' | 'id'>) => {
    try {
      const response = await vehicleOrderAPI.create(orderData);
      setOrders([response.order, ...orders]);
      setShowOrderForm(false);
      alert('âœ… Order created successfully!');
      loadOrders(); // Refresh the list
    } catch (error: any) {
      console.error('Error creating order:', error);
      alert(`âŒ Failed to create order: ${error.message}`);
    }
  };

  const handleUpdateOrder = async (orderId: string, orderData: Partial<VehicleOrder>) => {
    try {
      const response = await vehicleOrderAPI.update(orderId, orderData);
      setOrders(orders.map(o => (o._id === orderId || o.id === orderId) ? response.order : o));
      setEditingOrder(null);
      alert('âœ… Order updated successfully!');
    } catch (error: any) {
      console.error('Error updating order:', error);
      alert(`âŒ Failed to update order: ${error.message}`);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-LK', {
      style: 'currency',
      currency: 'LKR',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      pending: '#ffc107',
      confirmed: '#2196f3',
      in_transit: '#9c27b0',
      arrived: '#4caf50',
      delivered: '#4caf50',
      cancelled: '#f44336'
    };
    return colors[status] || '#757575';
  };

  const getStatusIcon = (status: string) => {
    const icons: Record<string, string> = {
      pending: 'â³',
      confirmed: 'âœ…',
      in_transit: 'ðŸš¢',
      arrived: 'ðŸ“¦',
      delivered: 'ðŸš—',
      cancelled: 'âŒ'
    };
    return icons[status] || 'ðŸ“‹';
  };

  const handleMoveToInventory = (order: VehicleOrder) => {
    if (order.orderStatus !== 'arrived') {
      alert('Vehicle must be in "Arrived" status before moving to inventory!');
      return;
    }

    if (window.confirm(`Move ${order.vehicleDetails.brand} ${order.vehicleDetails.model} to inventory?`)) {
      onMoveToInventory(order);
      // Update order status to delivered
      setOrders(orders.map(o => 
        o.id === order.id 
          ? { ...o, orderStatus: 'delivered' as const, deliveryDate: new Date().toISOString().split('T')[0] }
          : o
      ));
      alert('Vehicle moved to inventory successfully!');
    }
  };

  const handleUpdateStatus = (orderId: string, newStatus: VehicleOrder['orderStatus']) => {
    setOrders(orders.map(order => {
      if (order.id === orderId) {
        const newTimeline = [...order.timeline, {
