      location: 'Dubai Showroom A',
      features: [],
      vin: '',
      licensePlate: '',
      fuelType: 'gasoline',
      transmission: 'automatic',
      engineSize: '',
      bodyType: 'sedan',
      supplier: '',
      purchaseDate: '',
      warrantyExpiry: '',
      notes: ''
    }
  );

  const [featuresInput, setFeaturesInput] = useState(
    initialData?.features?.join(', ') || ''
  );

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: ['year', 'mileage', 'purchasePrice', 'marketValue', 'sellingPrice'].includes(name) 
        ? parseFloat(value) || 0 
        : value
    }));
  };

  const handleFeaturesChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setFeaturesInput(e.target.value);
    const featuresArray = e.target.value.split(',').map(f => f.trim()).filter(f => f.length > 0);
    setFormData(prev => ({
      ...prev,
      features: featuresArray
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const calculatePotentialProfit = () => {
    if (formData.sellingPrice && formData.sellingPrice > 0) {
      return formData.sellingPrice - formData.purchasePrice;
    }
    return formData.marketValue - formData.purchasePrice;
  };

