                  <input
                    type="text"
                    id="color"
                    name="color"
                    value={formData.color}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                    placeholder="e.g., Pearl White"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="mileage" className="form-label">Mileage (km) *</label>
                  <input
                    type="number"
                    id="mileage"
                    name="mileage"
                    value={formData.mileage}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                    min="0"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="condition" className="form-label">Condition *</label>
                  <select
                    id="condition"
                    name="condition"
                    value={formData.condition}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                  >
                    <option value="excellent">Excellent</option>
                    <option value="good">Good</option>
                    <option value="fair">Fair</option>
                    <option value="needs_repair">Needs Repair</option>
                  </select>
                </div>
              </div>
            </div>

            {/* Technical Specifications */}
            <div className="form-section">
              <h3>Technical Specifications</h3>
              <div className="form-grid">
                <div className="form-group">
                  <label htmlFor="bodyType" className="form-label">Body Type *</label>
                  <select
                    id="bodyType"
                    name="bodyType"
                    value={formData.bodyType}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                  >
                    <option value="sedan">Sedan</option>
                    <option value="suv">SUV</option>
                    <option value="hatchback">Hatchback</option>
                    <option value="coupe">Coupe</option>
                    <option value="pickup">Pickup</option>
                    <option value="van">Van</option>
                    <option value="other">Other</option>
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="fuelType" className="form-label">Fuel Type *</label>
                  <select
                    id="fuelType"
                    name="fuelType"
                    value={formData.fuelType}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                  >
                    <option value="gasoline">Gasoline</option>
                    <option value="diesel">Diesel</option>
                    <option value="hybrid">Hybrid</option>
                    <option value="electric">Electric</option>
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="transmission" className="form-label">Transmission *</label>
                  <select
                    id="transmission"
                    name="transmission"
                    value={formData.transmission}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                  >
                    <option value="automatic">Automatic</option>
                    <option value="manual">Manual</option>
                    <option value="cvt">CVT</option>
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="engineSize" className="form-label">Engine Size</label>
                  <input
                    type="text"
                    id="engineSize"
                    name="engineSize"
                    value={formData.engineSize}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="e.g., 2.5L"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="vin" className="form-label">VIN Number</label>
                  <input
                    type="text"
                    id="vin"
                    name="vin"
                    value={formData.vin}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="Vehicle Identification Number"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="licensePlate" className="form-label">License Plate</label>
                  <input
                    type="text"
                    id="licensePlate"
                    name="licensePlate"
                    value={formData.licensePlate}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="e.g., D-12345"
                  />
                </div>
              </div>
            </div>

            {/* Financial Information */}
            <div className="form-section">
              <h3>Financial Information</h3>
              <div className="form-grid">
                <div className="form-group">
                  <label htmlFor="purchasePrice" className="form-label">Purchase Price *</label>
                  <input
                    type="number"
                    id="purchasePrice"
                    name="purchasePrice"
                    value={formData.purchasePrice}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                    min="0"
                    step="0.01"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="marketValue" className="form-label">Market Value *</label>
                  <input
                    type="number"
                    id="marketValue"
                    name="marketValue"
                    value={formData.marketValue}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                    min="0"
                    step="0.01"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="sellingPrice" className="form-label">Target Selling Price</label>
                  <input
                    type="number"
                    id="sellingPrice"
                    name="sellingPrice"
                    value={formData.sellingPrice}
                    onChange={handleInputChange}
                    className="form-input"
                    min="0"
                    step="0.01"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="currency" className="form-label">Currency *</label>
                  <select
                    id="currency"
                    name="currency"
                    value={formData.currency}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                  >
                    <option value="USD">USD ($)</option>
                    <option value="JPY">JPY (Â¥)</option>
                    <option value="EUR">EUR (â‚¬)</option>
                    <option value="GBP">GBP (Â£)</option>
                  </select>
                </div>
              </div>
            </div>

            {/* Location and Supplier Information */}
            <div className="form-section">
              <h3>Location & Supplier</h3>
              <div className="form-grid">
                <div className="form-group">
                  <label htmlFor="location" className="form-label">Location *</label>
                  <select
                    id="location"
                    name="location"
                    value={formData.location}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                  >
                    <option value="Dubai Showroom A">Dubai Showroom A</option>
                    <option value="Dubai Showroom B">Dubai Showroom B</option>
                    <option value="Abu Dhabi Branch">Abu Dhabi Branch</option>
                    <option value="Outdoor Lot">Outdoor Lot</option>
                    <option value="Special Collection">Special Collection</option>
                    <option value="Service Center">Service Center</option>
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="supplier" className="form-label">Supplier</label>
                  <input
                    type="text"
                    id="supplier"
                    name="supplier"
                    value={formData.supplier}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="Supplier name"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="purchaseDate" className="form-label">Purchase Date</label>
                  <input
                    type="date"
                    id="purchaseDate"
                    name="purchaseDate"
                    value={formData.purchaseDate}
                    onChange={handleInputChange}
                    className="form-input"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="warrantyExpiry" className="form-label">Warranty Expiry</label>
                  <input
                    type="date"
                    id="warrantyExpiry"
                    name="warrantyExpiry"
                    value={formData.warrantyExpiry}
                    onChange={handleInputChange}
                    className="form-input"
                  />
                </div>
              </div>
            </div>

            {/* Features */}
            <div className="form-section">
              <h3>Features & Notes</h3>
              <div className="form-group">
                <label htmlFor="features" className="form-label">Features</label>
                <textarea
                  id="features"
                  name="features"
                  value={featuresInput}
                  onChange={handleFeaturesChange}
                  className="form-input"
                  rows={3}
                  placeholder="Enter features separated by commas (e.g., Navigation System, Leather Seats, Sunroof)"
                />
                <small className="form-help">Separate multiple features with commas</small>
              </div>

              <div className="form-group">
                <label htmlFor="notes" className="form-label">Notes</label>
                <textarea
                  id="notes"
                  name="notes"
                  value={formData.notes}
                  onChange={handleInputChange}
                  className="form-input"
                  rows={4}
                  placeholder="Any additional notes about the vehicle..."
                />
              </div>
            </div>

            {/* Financial Summary */}
            <div className="form-section">
              <div className="cost-summary">
                <h3>Financial Summary</h3>
                <div className="cost-breakdown">
                  <div className="cost-item">
                    <span>Purchase Price:</span>
                    <span>{formData.currency} {formData.purchasePrice.toFixed(2)}</span>
                  </div>
                  <div className="cost-item">
                    <span>Market Value:</span>
                    <span>{formData.currency} {formData.marketValue.toFixed(2)}</span>
                  </div>
                  {formData.sellingPrice && formData.sellingPrice > 0 && (
                    <div className="cost-item">
                      <span>Target Selling Price:</span>
                      <span>{formData.currency} {formData.sellingPrice.toFixed(2)}</span>
                    </div>
                  )}
                  <div className="cost-item total">
                    <span><strong>Potential Profit:</strong></span>
                    <span className={calculatePotentialProfit() >= 0 ? 'profit-positive' : 'profit-negative'}>
                      <strong>{formData.currency} {calculatePotentialProfit().toFixed(2)}</strong>
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="form-actions">
            <button type="button" className="btn btn-secondary" onClick={onCancel}>
              Cancel
            </button>
            <button type="submit" className="btn btn-primary">
              {isEditing ? 'âœï¸ Update Vehicle' : 'ðŸš— Add Vehicle'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Dashboard;
