import React, { useState } from 'react';
import './ExpenseManager.css';

interface Expense {
  id: string;
  category: string;
  description: string;
  amount: number;
  date: string;
  paymentMethod: string;
  notes?: string;
  currency: 'USD' | 'LKR' | 'EUR' | 'JPY';
}

const EXPENSE_CATEGORIES = [
  'Electric Bill',
  'Water Bill',
  'Showroom Maintenance',
  'Rent',
  'Salaries',
  'Internet/Phone',
  'Office Supplies',
  'Insurance',
  'Marketing',
  'Fuel/Transport',
  'Others'
];

const ExpenseManager: React.FC = () => {
  const [expenses, setExpenses] = useState<Expense[]>([
    {
      id: 'EXP001',
      category: 'Electric Bill',
      description: 'Monthly electricity bill',
      amount: 15000,
      date: '2024-10-01',
      paymentMethod: 'Bank Transfer',
      currency: 'LKR'
    },
    {
      id: 'EXP002',
      category: 'Water Bill',
      description: 'Monthly water bill',
      amount: 3500,
      date: '2024-10-01',
      paymentMethod: 'Cash',
      currency: 'LKR'
    },
    {
      id: 'EXP003',
      category: 'Showroom Maintenance',
      description: 'Cleaning and maintenance services',
      amount: 25000,
      date: '2024-10-05',
      paymentMethod: 'Bank Transfer',
      currency: 'LKR'
    },
    {
      id: 'EXP004',
      category: 'Rent',
      description: 'Monthly showroom rent',
      amount: 150000,
      date: '2024-10-01',
      paymentMethod: 'Bank Transfer',
      currency: 'LKR'
    }
  ]);

  const [showExpenseForm, setShowExpenseForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState<string>('all');
  const [dateFilter, setDateFilter] = useState<'all' | 'today' | 'week' | 'month' | 'custom'>('all');
  const [customDateRange, setCustomDateRange] = useState({ start: '', end: '' });

  const [newExpense, setNewExpense] = useState<Omit<Expense, 'id'>>({
    category: '',
    description: '',
    amount: 0,
    date: new Date().toISOString().split('T')[0],
    paymentMethod: 'Cash',
    currency: 'LKR',
    notes: ''
  });

  const [customCategory, setCustomCategory] = useState('');

  const handleAddExpense = (e: React.FormEvent) => {
    e.preventDefault();

    // Use custom category if "Others" is selected
    const finalCategory = newExpense.category === 'Others' ? customCategory : newExpense.category;

    const expense: Expense = {
      ...newExpense,
      category: finalCategory,
      id: `EXP${(expenses.length + 1).toString().padStart(3, '0')}`
    };
    setExpenses([expense, ...expenses]);
    setShowExpenseForm(false);
    setCustomCategory('');
    setNewExpense({
      category: '',
      description: '',
      amount: 0,
      date: new Date().toISOString().split('T')[0],
      paymentMethod: 'Cash',
      currency: 'LKR',
      notes: ''
    });
  };

  const handleDeleteExpense = (id: string) => {
    if (window.confirm('Are you sure you want to delete this expense?')) {
      setExpenses(expenses.filter(exp => exp.id !== id));
    }
  };

  // Date filtering logic
  const filterByDate = (expense: Expense) => {
    if (dateFilter === 'all') return true;

    const expenseDate = new Date(expense.date);
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    if (dateFilter === 'today') {
      const expDate = new Date(expense.date);
      expDate.setHours(0, 0, 0, 0);
      return expDate.getTime() === today.getTime();
    }

    if (dateFilter === 'week') {
      const weekAgo = new Date(today);
      weekAgo.setDate(today.getDate() - 7);
      return expenseDate >= weekAgo;
    }

    if (dateFilter === 'month') {
      const monthAgo = new Date(today);
      monthAgo.setMonth(today.getMonth() - 1);
      return expenseDate >= monthAgo;
    }

    if (dateFilter === 'custom' && customDateRange.start && customDateRange.end) {
      const start = new Date(customDateRange.start);
      const end = new Date(customDateRange.end);
      return expenseDate >= start && expenseDate <= end;
    }

    return true;
  };

  // Filtered expenses
  const filteredExpenses = expenses.filter(expense => {
    const matchesSearch = expense.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          expense.category.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = categoryFilter === 'all' || expense.category === categoryFilter;
    const matchesDate = filterByDate(expense);

    return matchesSearch && matchesCategory && matchesDate;
  });

  // Calculate totals
  const totalExpenses = filteredExpenses.reduce((sum, exp) => sum + exp.amount, 0);
  const categoryTotals = filteredExpenses.reduce((acc, exp) => {
    acc[exp.category] = (acc[exp.category] || 0) + exp.amount;
    return acc;
  }, {} as Record<string, number>);

  const formatCurrency = (amount: number, currency: string = 'LKR') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: 2
    }).format(amount);
  };

  return (
    <div className="expense-manager">
      {/* Header */}
      <div className="expense-header">
        <div>
          <h2>üí∞ Expense Management</h2>
          <p className="subtitle">Track and manage your business expenses</p>
        </div>
        <button 
          className="btn btn-primary"
          onClick={() => setShowExpenseForm(true)}
        >
          ‚ûï Add Expense
        </button>
      </div>

      {/* Summary Cards */}
      <div className="expense-summary-grid">
        <div className="summary-card total">
          <div className="summary-icon">üíµ</div>
          <div className="summary-content">
            <h3>{formatCurrency(totalExpenses)}</h3>
            <p>Total Expenses</p>
          </div>
        </div>
        <div className="summary-card count">
          <div className="summary-icon">üìä</div>
          <div className="summary-content">
            <h3>{filteredExpenses.length}</h3>
            <p>Total Records</p>
          </div>
        </div>
        <div className="summary-card average">
          <div className="summary-icon">üìà</div>
          <div className="summary-content">
            <h3>{formatCurrency(filteredExpenses.length > 0 ? totalExpenses / filteredExpenses.length : 0)}</h3>
            <p>Average Expense</p>
          </div>
        </div>
        <div className="summary-card categories">
          <div className="summary-icon">üìë</div>
          <div className="summary-content">
            <h3>{Object.keys(categoryTotals).length}</h3>
            <p>Categories</p>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="expense-filters">
        <div className="filter-row">
          <input
            type="text"
            className="search-input"
            placeholder="üîç Search expenses..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />

          <select
            className="filter-select"
            value={categoryFilter}
            onChange={(e) => setCategoryFilter(e.target.value)}
          >
            <option value="all">All Categories</option>
            {EXPENSE_CATEGORIES.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>

          <select
            className="filter-select"
            value={dateFilter}
            onChange={(e) => setDateFilter(e.target.value as any)}
          >
            <option value="all">All Time</option>
            <option value="today">Today</option>
            <option value="week">This Week</option>
            <option value="month">This Month</option>
            <option value="custom">Custom Range</option>
          </select>

          {dateFilter === 'custom' && (
            <>
              <input
                type="date"
                className="date-input"
                value={customDateRange.start}
                onChange={(e) => setCustomDateRange({...customDateRange, start: e.target.value})}
                placeholder="Start Date"
              />
              <input
                type="date"
                className="date-input"
                value={customDateRange.end}
                onChange={(e) => setCustomDateRange({...customDateRange, end: e.target.value})}
                placeholder="End Date"
              />
            </>
          )}
        </div>
      </div>

      {/* Expense List */}
      <div className="expense-list-container">
        {filteredExpenses.length > 0 ? (
          <table className="expense-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Category</th>
                <th>Description</th>
                <th>Amount</th>
                <th>Payment Method</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredExpenses.map(expense => (
                <tr key={expense.id}>
                  <td>{new Date(expense.date).toLocaleDateString()}</td>
                  <td>
                    <span className="category-badge">{expense.category}</span>
                  </td>
                  <td>
                    <div className="expense-desc">
                      <strong>{expense.description}</strong>
                      {expense.notes && <small>{expense.notes}</small>}
                    </div>
                  </td>
                  <td className="amount-cell">
                    <strong>{formatCurrency(expense.amount, expense.currency)}</strong>
                  </td>
                  <td>{expense.paymentMethod}</td>
                  <td>
                    <button 
                      className="btn btn-danger btn-sm"
                      onClick={() => handleDeleteExpense(expense.id)}
                      title="Delete"
                    >
                      üóëÔ∏è
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="no-expenses">
            <p>üì≠ No expenses found. Add your first expense to get started!</p>
          </div>
        )}
      </div>

      {/* Expense Form Modal */}
      {showExpenseForm && (
        <div className="vehicle-order-form-overlay">
          <div className="vehicle-order-form">
            <div className="form-header">
              <h2>‚ûï Add New Expense</h2>
              <button className="close-btn" onClick={() => setShowExpenseForm(false)}>√ó</button>
            </div>

            <form onSubmit={handleAddExpense}>
              <div className="form-sections">
                <div className="form-section">
                  <div className="form-grid">
                    <div className="form-group">
                      <label>Category *</label>
                      <select
                        className="form-input"
                        value={newExpense.category}
                        onChange={(e) => {
                          setNewExpense({...newExpense, category: e.target.value});
                          if (e.target.value !== 'Others') {
                            setCustomCategory('');
                          }
                        }}
                        required
                      >
                        <option value="">Select Category</option>
                        {EXPENSE_CATEGORIES.map(cat => (
                          <option key={cat} value={cat}>{cat}</option>
                        ))}
                      </select>
                    </div>

                    {newExpense.category === 'Others' && (
                      <div className="form-group">
                        <label>Custom Category Name *</label>
                        <input
                          type="text"
                          className="form-input"
                          value={customCategory}
                          onChange={(e) => setCustomCategory(e.target.value)}
                          placeholder="Enter custom category name"
                          required
                        />
                      </div>
                    )}

                    <div className="form-group">
                      <label>Date *</label>
                      <input
                        type="date"
                        className="form-input"
                        value={newExpense.date}
                        onChange={(e) => setNewExpense({...newExpense, date: e.target.value})}
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label>Amount *</label>
                      <input
                        type="number"
                        className="form-input"
                        value={newExpense.amount}
                        onChange={(e) => setNewExpense({...newExpense, amount: parseFloat(e.target.value) || 0})}
                        min="0"
                        step="0.01"
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label>Currency *</label>
                      <select
                        className="form-input"
                        value={newExpense.currency}
                        onChange={(e) => setNewExpense({...newExpense, currency: e.target.value as any})}
                        required
                      >
                        <option value="LKR">LKR (Rs.)</option>
                        <option value="USD">USD ($)</option>
                        <option value="EUR">EUR (‚Ç¨)</option>
                        <option value="JPY">JPY (¬•)</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label>Payment Method *</label>
                      <select
                        className="form-input"
                        value={newExpense.paymentMethod}
                        onChange={(e) => setNewExpense({...newExpense, paymentMethod: e.target.value})}
                        required
                      >
                        <option value="Cash">Cash</option>
                        <option value="Bank Transfer">Bank Transfer</option>
                        <option value="Credit Card">Credit Card</option>
                        <option value="Debit Card">Debit Card</option>
                        <option value="Cheque">Cheque</option>
                      </select>
                    </div>

                    <div className="form-group full-width">
                      <label>Description *</label>
                      <input
                        type="text"
                        className="form-input"
                        value={newExpense.description}
                        onChange={(e) => setNewExpense({...newExpense, description: e.target.value})}
                        placeholder="Enter expense description"
                        required
                      />
                    </div>

                    <div className="form-group full-width">
                      <label>Notes (Optional)</label>
                      <textarea
                        className="form-input"
                        value={newExpense.notes}
                        onChange={(e) => setNewExpense({...newExpense, notes: e.target.value})}
                        rows={3}
                        placeholder="Additional notes..."
                      />
                    </div>
                  </div>
                </div>
              </div>

              <div className="form-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary" 
                  onClick={() => setShowExpenseForm(false)}
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  Add Expense
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpenseManager;
