import React, { useState } from 'react';
import './Dashboard.css';
import VehicleOrderForm from './VehicleOrderForm';

interface VehicleOrder {
  id: string;
  model: string;
  year: number;
  country: string;
  orderDate: string;
  status: 'ordered' | 'shipped' | 'clearing' | 'completed';
  totalCost: number;
  expenses: {
    vehicleCost: number;
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    cno: number;
    cif: number;
  };
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  profit?: number;
  sellingPrice?: number;
  supplier?: string;
  expectedDelivery?: string;
  paymentMethod?: string;
  notes?: string;
}

interface VehicleOrderFormData {
  model: string;
  year: number;
  country: string;
  supplier: string;
  vehicleCost: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  expectedDelivery: string;
  notes: string;
  expenses: {
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    cno: number;
    cif: number;
  };
  paymentMethod: string;
}

const Dashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [showOrderForm, setShowOrderForm] = useState(false);
  const [editingOrder, setEditingOrder] = useState<VehicleOrder | null>(null);
  const [orderDetailsModal, setOrderDetailsModal] = useState<VehicleOrder | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  
  // Sample data
  const [vehicleOrders, setVehicleOrders] = useState<VehicleOrder[]>([
    {
      id: 'ORD001',
      model: 'Toyota Camry',
      year: 2023,
      country: 'Japan',
      orderDate: '2024-01-15',
      status: 'completed',
      totalCost: 25000,
      expenses: {
        vehicleCost: 20000,
        fuel: 500,
        duty: 2000,
        driverCharge: 800,
        clearanceCharge: 600,
        demurrage: 300,
        tax: 600,
        cno: 100,
        cif: 100
      },
      currency: 'USD',
      profit: 5000,
      sellingPrice: 30000,
      supplier: 'Tokyo Auto Exports',
      expectedDelivery: '2024-03-15',
      paymentMethod: 'Bank Transfer',
      notes: 'Low mileage, excellent condition'
    },
    {
      id: 'ORD002',
      model: 'Honda Accord',
      year: 2023,
      country: 'Japan',
      orderDate: '2024-02-10',
      status: 'clearing',
      totalCost: 23000,
      expenses: {
        vehicleCost: 18000,
        fuel: 450,
        duty: 1800,
        driverCharge: 750,
        clearanceCharge: 550,
        demurrage: 250,
        tax: 550,
        cno: 100,
        cif: 550
      },
      currency: 'USD',
      supplier: 'Osaka Motors Ltd',
      expectedDelivery: '2024-04-20',
      paymentMethod: 'Letter of Credit',
      notes: 'Premium package, navigation system included'
    },
    {
      id: 'ORD003',
      model: 'BMW X5',
      year: 2022,
      country: 'Germany',
      orderDate: '2024-01-25',
      status: 'shipped',
      totalCost: 45000,
      expenses: {
        vehicleCost: 38000,
        fuel: 800,
        duty: 3500,
        driverCharge: 1200,
        clearanceCharge: 900,
        demurrage: 0,
        tax: 1100,
        cno: 150,
        cif: 350
      },
      currency: 'EUR',
      supplier: 'Munich Auto GmbH',
      expectedDelivery: '2024-03-30',
      paymentMethod: 'Bank Transfer',
      notes: 'Luxury SUV, full options'
    },
    {
      id: 'ORD004',
      model: 'Nissan GTR',
      year: 2024,
      country: 'Japan',
      orderDate: '2024-03-01',
      status: 'ordered',
      totalCost: 85000,
      expenses: {
        vehicleCost: 75000,
        fuel: 600,
        duty: 5000,
        driverCharge: 1000,
        clearanceCharge: 800,
        demurrage: 0,
        tax: 2400,
        cno: 100,
        cif: 100
      },
      currency: 'JPY',
      supplier: 'Yokohama Sport Cars',
      expectedDelivery: '2024-05-15',
      paymentMethod: 'Bank Transfer',
      notes: 'Performance sports car, track package'
    }
  ]);

  // Handler functions
  const handleNewOrder = (formData: VehicleOrderFormData) => {
    const newOrder: VehicleOrder = {
      id: `ORD${String(vehicleOrders.length + 1).padStart(3, '0')}`,
      model: formData.model,
      year: formData.year,
      country: formData.country,
      orderDate: new Date().toISOString().split('T')[0],
      status: 'ordered',
      totalCost: formData.vehicleCost + Object.values(formData.expenses).reduce((sum, val) => sum + val, 0),
      expenses: {
        vehicleCost: formData.vehicleCost,
        ...formData.expenses
      },
      currency: formData.currency,
      supplier: formData.supplier,
      expectedDelivery: formData.expectedDelivery,
      paymentMethod: formData.paymentMethod,
      notes: formData.notes
    };
    
    setVehicleOrders([...vehicleOrders, newOrder]);
    setShowOrderForm(false);
    setActiveTab('orders');
  };

  const handleEditOrder = (order: VehicleOrder) => {
    setEditingOrder(order);
    setShowOrderForm(true);
  };

  const handleUpdateOrder = (formData: VehicleOrderFormData) => {
    if (!editingOrder) return;
    
    const updatedOrder: VehicleOrder = {
      ...editingOrder,
      model: formData.model,
      year: formData.year,
      country: formData.country,
      totalCost: formData.vehicleCost + Object.values(formData.expenses).reduce((sum, val) => sum + val, 0),
      expenses: {
        vehicleCost: formData.vehicleCost,
        ...formData.expenses
      },
      currency: formData.currency,
      supplier: formData.supplier,
      expectedDelivery: formData.expectedDelivery,
      paymentMethod: formData.paymentMethod,
      notes: formData.notes
    };
    
    setVehicleOrders(vehicleOrders.map(order => 
      order.id === editingOrder.id ? updatedOrder : order
    ));
    setShowOrderForm(false);
    setEditingOrder(null);
  };

  const handleDeleteOrder = (orderId: string) => {
    if (confirm('Are you sure you want to delete this order?')) {
      setVehicleOrders(vehicleOrders.filter(order => order.id !== orderId));
    }
  };

  const handleUpdateStatus = (orderId: string, newStatus: 'ordered' | 'shipped' | 'clearing' | 'completed') => {
    setVehicleOrders(vehicleOrders.map(order => 
      order.id === orderId ? { ...order, status: newStatus } : order
    ));
  };

  const handleFormCancel = () => {
    setShowOrderForm(false);
    setEditingOrder(null);
  };

  const handleFormSubmit = (formData: VehicleOrderFormData) => {
    if (editingOrder) {
      handleUpdateOrder(formData);
    } else {
      handleNewOrder(formData);
    }
  };

  // Filter orders based on search and status
  const filteredOrders = vehicleOrders.filter(order => {
    const matchesSearch = order.model.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         order.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         order.country.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (order.supplier && order.supplier.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = statusFilter === 'all' || order.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const getStatusBadge = (status: string) => {
    const statusMap = {
      ordered: 'badge-info',
      shipped: 'badge-warning',
      clearing: 'badge-warning',
      completed: 'badge-success'
    };
    return statusMap[status as keyof typeof statusMap] || 'badge-info';
  };

  const formatCurrency = (amount: number, currency: string = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const totalAssets = vehicleOrders.reduce((sum, order) => sum + order.totalCost, 0);
  const totalProfit = vehicleOrders.reduce((sum, order) => sum + (order.profit || 0), 0);
  const completedOrders = vehicleOrders.filter(order => order.status === 'completed').length;
  const pendingOrders = vehicleOrders.filter(order => order.status !== 'completed').length;

  return (
    <div className="dashboard">
      {/* Key Metrics */}
      <div className="metrics-grid grid grid-4">
        <div className="card metric-card">
          <div className="metric-icon assets"></div>
          <div className="metric-info">
            <h3 className="metric-value">{formatCurrency(totalAssets)}</h3>
            <p className="metric-label">Total Assets</p>
          </div>
        </div>
        
        <div className="card metric-card">
          <div className="metric-icon profit"></div>
          <div className="metric-info">
            <h3 className="metric-value">{formatCurrency(totalProfit)}</h3>
            <p className="metric-label">Total Profit</p>
          </div>
        </div>
        
        <div className="card metric-card">
          <div className="metric-icon completed"></div>
          <div className="metric-info">
            <h3 className="metric-value">{completedOrders}</h3>
            <p className="metric-label">Completed Orders</p>
          </div>
        </div>
        
        <div className="card metric-card">
          <div className="metric-icon pending"></div>
          <div className="metric-info">
            <h3 className="metric-value">{pendingOrders}</h3>
            <p className="metric-label">Pending Orders</p>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button 
          className={`tab-btn ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => setActiveTab('orders')}
        >
          Vehicle Orders
        </button>
        <button 
          className={`tab-btn ${activeTab === 'expenses' ? 'active' : ''}`}
          onClick={() => setActiveTab('expenses')}
        >
          Expense Tracking
        </button>
        <button 
          className={`tab-btn ${activeTab === 'reports' ? 'active' : ''}`}
          onClick={() => setActiveTab('reports')}
        >
          Reports
        </button>
      </div>

      {/* Tab Content */}
      {activeTab === 'overview' && (
        <div className="tab-content">
          <div className="grid grid-2">
            <div className="card">
              <h3>Recent Orders</h3>
              <div className="order-list">
                {vehicleOrders.slice(0, 3).map(order => (
                  <div key={order.id} className="order-item">
                    <div className="order-details">
                      <h4>{order.model} ({order.year})</h4>
                      <p>Order ID: {order.id}</p>
                      <p>From: {order.country}</p>
                    </div>
                    <div className="order-status">
                      <span className={`badge ${getStatusBadge(order.status)}`}>
                        {order.status.toUpperCase()}
                      </span>
                      <p className="order-cost">{formatCurrency(order.totalCost, order.currency)}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            
            <div className="card">
              <h3>Quick Actions</h3>
              <div className="action-buttons">
                <button className="btn btn-primary" onClick={() => {setShowOrderForm(true); setEditingOrder(null);}}>New Vehicle Order</button>
                <button className="btn btn-secondary" onClick={() => setActiveTab('expenses')}>Add Expense</button>
                <button className="btn btn-secondary" onClick={() => setActiveTab('reports')}>Generate Report</button>
                <button className="btn btn-secondary" onClick={() => setActiveTab('orders')}>View All Orders</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'orders' && (
        <div className="tab-content">
          <div className="card">
            <div className="table-header">
              <h3>Vehicle Orders Management</h3>
              <button className="btn btn-primary">Add New Order</button>
            </div>
            <table className="table">
              <thead>
                <tr>
                  <th>Order ID</th>
                  <th>Vehicle</th>
                  <th>Country</th>
                  <th>Order Date</th>
                  <th>Status</th>
                  <th>Total Cost</th>
                  <th>Profit</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {vehicleOrders.map(order => (
                  <tr key={order.id}>
                    <td>{order.id}</td>
                    <td>{order.model} ({order.year})</td>
                    <td>{order.country}</td>
                    <td>{order.orderDate}</td>
                    <td>
                      <span className={`badge ${getStatusBadge(order.status)}`}>
                        {order.status.toUpperCase()}
                      </span>
                    </td>
                    <td>{formatCurrency(order.totalCost, order.currency)}</td>
                    <td>{order.profit ? formatCurrency(order.profit, order.currency) : '-'}</td>
                    <td>
                      <button className="btn btn-secondary btn-sm">Edit</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {activeTab === 'expenses' && (
        <div className="tab-content">
          <div className="card">
            <h3>Expense Breakdown</h3>
            <div className="expense-categories grid grid-3">
              {vehicleOrders[0] && Object.entries(vehicleOrders[0].expenses).map(([key, value]) => (
                <div key={key} className="expense-item">
                  <h4>{key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}</h4>
                  <p className="expense-amount">{formatCurrency(value)}</p>
                </div>
              ))}
            </div>
          </div>
          
          <div className="grid grid-2">
            <div className="card">
              <h3>CNO & CIF Tracking</h3>
              <div className="document-tracking">
                <div className="doc-item">
                  <span className="doc-label">CNO Status:</span>
                  <span className="badge badge-success">Approved</span>
                </div>
                <div className="doc-item">
                  <span className="doc-label">CIF Value:</span>
                  <span className="doc-value">{formatCurrency(550)}</span>
                </div>
              </div>
            </div>
            
            <div className="card">
              <h3>Payment Methods</h3>
              <div className="payment-methods">
                <div className="payment-item">
                  <span className="payment-label">Demurrage Payment:</span>
                  <span className="payment-method">Bank Transfer</span>
                </div>
                <div className="payment-item">
                  <span className="payment-label">Currency Status:</span>
                  <span className="badge badge-info">USD</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'reports' && (
        <div className="tab-content">
          <div className="grid grid-2">
            <div className="card">
              <h3>Monthly Reports</h3>
              <div className="report-list">
                <div className="report-item">
                  <h4>September 2024</h4>
                  <p>Total Sales: {formatCurrency(48000)}</p>
                  <p>Total Profit: {formatCurrency(5000)}</p>
                  <button className="btn btn-secondary btn-sm">Download PDF</button>
                </div>
                <div className="report-item">
                  <h4>August 2024</h4>
                  <p>Total Sales: {formatCurrency(35000)}</p>
                  <p>Total Profit: {formatCurrency(3500)}</p>
                  <button className="btn btn-secondary btn-sm">Download PDF</button>
                </div>
              </div>
            </div>
            
            <div className="card">
              <h3>Analytics Summary</h3>
              <div className="analytics">
                <div className="analytics-item">
                  <span className="analytics-label">Average Profit Margin:</span>
                  <span className="analytics-value">20.8%</span>
                </div>
                <div className="analytics-item">
                  <span className="analytics-label">Best Performing Country:</span>
                  <span className="analytics-value">Japan</span>
                </div>
                <div className="analytics-item">
                  <span className="analytics-label">Total Vehicles in Stock:</span>
                  <span className="analytics-value">12</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
