import React, { useState } from 'react';
import './Dashboard.css';
import VehicleOrderForm from './VehicleOrderForm';

interface VehicleOrder {
  id: string;
  model: string;
  year: number;
  country: string;
  orderDate: string;
  status: 'ordered' | 'shipped' | 'clearing' | 'completed' | 'sold';
  totalCost: number;
  expenses: {
    vehicleCost: number;
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    cno: number;
    cif: number;
  };
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  profit?: number;
  sellingPrice?: number;
  supplier?: string;
  expectedDelivery?: string;
  paymentMethod?: string;
  notes?: string;
  // Advanced fields
  customerInfo?: {
    name: string;
    contact: string;
    paymentMethod: string;
  };
  profitMargin?: number;
  saleDate?: string;
  marketValue?: number;
  depreciation?: number;
}

interface InventoryItem {
  id: string;
  model: string;
  brand: string;
  year: number;
  color: string;
  mileage: number;
  condition: 'excellent' | 'good' | 'fair' | 'needs_repair';
  purchasePrice: number;
  marketValue: number;
  sellingPrice?: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  location: string;
  status: 'available' | 'reserved' | 'sold' | 'maintenance';
  dateAdded: string;
  lastUpdated: string;
  features: string[];
  images?: string[];
  vin?: string;
  licensePlate?: string;
  fuelType: 'gasoline' | 'diesel' | 'hybrid' | 'electric';
  transmission: 'manual' | 'automatic' | 'cvt';
  engineSize?: string;
  bodyType: 'sedan' | 'suv' | 'hatchback' | 'coupe' | 'pickup' | 'van' | 'other';
  supplier?: string;
  purchaseDate?: string;
  warrantyExpiry?: string;
  notes?: string;
  reservedBy?: {
    name: string;
    contact: string;
    reservationDate: string;
    depositAmount?: number;
  };
  soldInfo?: {
    customerName: string;
    customerContact: string;
    saleDate: string;
    finalPrice: number;
    paymentMethod: string;
  };
}

interface InventoryFormData {
  model: string;
  brand: string;
  year: number;
  color: string;
  mileage: number;
  condition: 'excellent' | 'good' | 'fair' | 'needs_repair';
  purchasePrice: number;
  marketValue: number;
  sellingPrice?: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  location: string;
  features: string[];
  vin?: string;
  licensePlate?: string;
  fuelType: 'gasoline' | 'diesel' | 'hybrid' | 'electric';
  transmission: 'manual' | 'automatic' | 'cvt';
  engineSize?: string;
  bodyType: 'sedan' | 'suv' | 'hatchback' | 'coupe' | 'pickup' | 'van' | 'other';
  supplier?: string;
  purchaseDate?: string;
  warrantyExpiry?: string;
  notes?: string;
}

interface VehicleOrderFormData {
  model: string;
  year: number;
  country: string;
  supplier: string;
  vehicleCost: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  expectedDelivery: string;
  notes: string;
  expenses: {
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    cno: number;
    cif: number;
  };
  paymentMethod: string;
}

interface DashboardProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ activeTab, onTabChange }) => {
  const [showOrderForm, setShowOrderForm] = useState(false);
  const [editingOrder, setEditingOrder] = useState<VehicleOrder | null>(null);
  const [orderDetailsModal, setOrderDetailsModal] = useState<VehicleOrder | null>(null);
  const [saleModal, setSaleModal] = useState<VehicleOrder | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');

  // Inventory management state
  const [showInventoryForm, setShowInventoryForm] = useState(false);
  const [editingInventoryItem, setEditingInventoryItem] = useState<InventoryItem | null>(null);
  const [inventoryDetailsModal, setInventoryDetailsModal] = useState<InventoryItem | null>(null);
  const [inventorySearchTerm, setInventorySearchTerm] = useState('');
  const [inventoryStatusFilter, setInventoryStatusFilter] = useState<string>('all');
  const [inventoryConditionFilter, setInventoryConditionFilter] = useState<string>('all');

  // Sample data
  const [vehicleOrders, setVehicleOrders] = useState<VehicleOrder[]>([
    {
      id: 'ORD001',
      model: 'Toyota Camry',
      year: 2023,
      country: 'Japan',
      orderDate: '2024-01-15',
      status: 'sold',
      totalCost: 25000,
      expenses: {
        vehicleCost: 20000,
        fuel: 500,
        duty: 2000,
        driverCharge: 800,
        clearanceCharge: 600,
        demurrage: 300,
        tax: 600,
        cno: 100,
        cif: 100
      },
      currency: 'USD',
      sellingPrice: 32000,
      supplier: 'Tokyo Auto Exports',
      expectedDelivery: '2024-03-15',
      paymentMethod: 'Bank Transfer',
      notes: 'Low mileage, excellent condition',
      customerInfo: {
        name: 'Ahmed Hassan',
        contact: '+971-50-123-4567',
        paymentMethod: 'Bank Transfer'
      },
      saleDate: '2024-04-10',
      marketValue: 31000
    },
    {
      id: 'ORD002',
      model: 'Honda Accord',
      year: 2023,
      country: 'Japan',
      orderDate: '2024-02-10',
      status: 'clearing',
      totalCost: 23000,
      expenses: {
        vehicleCost: 18000,
        fuel: 450,
        duty: 1800,
        driverCharge: 750,
        clearanceCharge: 550,
        demurrage: 250,
        tax: 550,
        cno: 100,
        cif: 550
      },
      currency: 'USD',
      supplier: 'Osaka Motors Ltd',
      expectedDelivery: '2024-04-20',
      paymentMethod: 'Letter of Credit',
      notes: 'Premium package, navigation system included'
    },
    {
      id: 'ORD003',
      model: 'BMW X5',
      year: 2022,
      country: 'Germany',
      orderDate: '2024-01-25',
      status: 'shipped',
      totalCost: 45000,
      expenses: {
        vehicleCost: 38000,
        fuel: 800,
        duty: 3500,
        driverCharge: 1200,
        clearanceCharge: 900,
        demurrage: 0,
        tax: 1100,
        cno: 150,
        cif: 350
      },
      currency: 'EUR',
      supplier: 'Munich Auto GmbH',
      expectedDelivery: '2024-03-30',
      paymentMethod: 'Bank Transfer',
      notes: 'Luxury SUV, full options'
    },
    {
      id: 'ORD004',
      model: 'Nissan GTR',
      year: 2024,
      country: 'Japan',
      orderDate: '2024-03-01',
      status: 'ordered',
      totalCost: 85000,
      expenses: {
        vehicleCost: 75000,
        fuel: 600,
        duty: 5000,
        driverCharge: 1000,
        clearanceCharge: 800,
        demurrage: 0,
        tax: 2400,
        cno: 100,
        cif: 100
      },
      currency: 'JPY',
      supplier: 'Yokohama Sport Cars',
      expectedDelivery: '2024-05-15',
      paymentMethod: 'Bank Transfer',
      notes: 'Performance sports car, track package'
    }
  ]);

  // Sample inventory data
  const [inventoryItems, setInventoryItems] = useState<InventoryItem[]>([
    {
      id: 'INV001',
      model: 'Camry Hybrid',
      brand: 'Toyota',
      year: 2023,
      color: 'Pearl White',
      mileage: 15000,
      condition: 'excellent',
      purchasePrice: 24000,
      marketValue: 28000,
      sellingPrice: 32000,
      currency: 'USD',
      location: 'Dubai Showroom A',
      status: 'available',
      dateAdded: '2024-01-15',
      lastUpdated: '2024-09-15',
      features: ['Hybrid Engine', 'Navigation System', 'Leather Seats', 'Sunroof', 'Backup Camera'],
      vin: '1HGCM82633A123456',
      licensePlate: 'D-12345',
      fuelType: 'hybrid',
      transmission: 'automatic',
      engineSize: '2.5L',
      bodyType: 'sedan',
      supplier: 'Tokyo Auto Exports',
      purchaseDate: '2024-01-10',
      warrantyExpiry: '2026-01-10',
      notes: 'Pristine condition, single owner, full service history'
    },
    {
      id: 'INV002',
      model: 'X5 xDrive40i',
      brand: 'BMW',
      year: 2022,
      color: 'Jet Black',
      mileage: 25000,
      condition: 'excellent',
      purchasePrice: 42000,
      marketValue: 48000,
      sellingPrice: 52000,
      currency: 'USD',
      location: 'Dubai Showroom A',
      status: 'reserved',
      dateAdded: '2024-02-20',
      lastUpdated: '2024-09-10',
      features: ['All-Wheel Drive', 'Premium Package', 'Panoramic Roof', 'Apple CarPlay', 'Heated Seats'],
      vin: '5UXCR6C50M0123456',
      licensePlate: 'D-67890',
      fuelType: 'gasoline',
      transmission: 'automatic',
      engineSize: '3.0L Turbo',
      bodyType: 'suv',
      supplier: 'Munich Auto GmbH',
      purchaseDate: '2024-02-15',
      warrantyExpiry: '2025-02-15',
      notes: 'Luxury SUV with premium features',
      reservedBy: {
        name: 'Sarah Johnson',
        contact: '+971-55-987-6543',
        reservationDate: '2024-09-10',
        depositAmount: 5000
      }
    },
    {
      id: 'INV003',
      model: 'GTR Nismo',
      brand: 'Nissan',
      year: 2024,
      color: 'Bayside Blue',
      mileage: 5000,
      condition: 'excellent',
      purchasePrice: 180000,
      marketValue: 195000,
      sellingPrice: 210000,
      currency: 'USD',
      location: 'Special Collection',
      status: 'available',
      dateAdded: '2024-03-01',
      lastUpdated: '2024-09-01',
      features: ['Track Package', 'Carbon Fiber Body', 'Racing Seats', 'Premium Audio', 'Launch Control'],
      vin: 'JN1AR5EF6PM123456',
      licensePlate: 'D-GTR24',
      fuelType: 'gasoline',
      transmission: 'automatic',
      engineSize: '3.8L Twin-Turbo V6',
      bodyType: 'coupe',
      supplier: 'Yokohama Sport Cars',
      purchaseDate: '2024-02-25',
      warrantyExpiry: '2027-02-25',
      notes: 'Limited edition performance vehicle, museum quality'
    },
    {
      id: 'INV004',
      model: 'Model S Plaid',
      brand: 'Tesla',
      year: 2023,
      color: 'Midnight Silver',
      mileage: 8000,
      condition: 'good',
      purchasePrice: 95000,
      marketValue: 105000,
      currency: 'USD',
      location: 'Dubai Showroom B',
      status: 'sold',
      dateAdded: '2024-01-30',
      lastUpdated: '2024-08-15',
      features: ['Autopilot', 'Premium Interior', 'Glass Roof', 'Supercharging', 'Over-the-Air Updates'],
      vin: '5YJ3E1EB4MF123456',
      licensePlate: 'D-TESLA',
      fuelType: 'electric',
      transmission: 'automatic',
      engineSize: 'Electric Motor',
      bodyType: 'sedan',
      supplier: 'Tesla Direct',
      purchaseDate: '2024-01-25',
      warrantyExpiry: '2027-01-25',
      notes: 'High-performance electric sedan',
      soldInfo: {
        customerName: 'Ahmed Al-Rashid',
        customerContact: '+971-50-111-2222',
        saleDate: '2024-08-15',
        finalPrice: 110000,
        paymentMethod: 'Bank Transfer'
      }
    },
    {
      id: 'INV005',
      model: 'Wrangler Rubicon',
      brand: 'Jeep',
      year: 2023,
      color: 'Desert Tan',
      mileage: 18000,
      condition: 'good',
      purchasePrice: 38000,
      marketValue: 42000,
      sellingPrice: 45000,
      currency: 'USD',
      location: 'Outdoor Lot',
      status: 'maintenance',
      dateAdded: '2024-03-10',
      lastUpdated: '2024-09-05',
      features: ['4x4 Capability', 'Removable Doors', 'Rock Rails', 'Skid Plates', 'LED Lighting'],
      vin: '1C4HJXFG1NW123456',
      licensePlate: 'D-JEEP1',
      fuelType: 'gasoline',
      transmission: 'manual',
      engineSize: '3.6L V6',
      bodyType: 'suv',
      supplier: 'American Motors LLC',
      purchaseDate: '2024-03-05',
      warrantyExpiry: '2026-03-05',
      notes: 'Off-road capable SUV, currently in for routine maintenance'
    }
  ]);

  // Handler functions
  const handleNewOrder = (formData: VehicleOrderFormData) => {
    const newOrder: VehicleOrder = {
      id: `ORD${String(vehicleOrders.length + 1).padStart(3, '0')}`,
      model: formData.model,
      year: formData.year,
      country: formData.country,
      orderDate: new Date().toISOString().split('T')[0],
      status: 'ordered',
      totalCost: formData.vehicleCost + Object.values(formData.expenses).reduce((sum, val) => sum + val, 0),
      expenses: {
        vehicleCost: formData.vehicleCost,
        ...formData.expenses
      },
      currency: formData.currency,
      supplier: formData.supplier,
      expectedDelivery: formData.expectedDelivery,
      paymentMethod: formData.paymentMethod,
      notes: formData.notes
    };

    setVehicleOrders([...vehicleOrders, newOrder]);
    setShowOrderForm(false);
    onTabChange('orders');
  };

  const handleEditOrder = (order: VehicleOrder) => {
    setEditingOrder(order);
    setShowOrderForm(true);
  };

  const handleUpdateOrder = (formData: VehicleOrderFormData) => {
    if (!editingOrder) return;

    const updatedOrder: VehicleOrder = {
      ...editingOrder,
      model: formData.model,
      year: formData.year,
      country: formData.country,
      totalCost: formData.vehicleCost + Object.values(formData.expenses).reduce((sum, val) => sum + val, 0),
      expenses: {
        vehicleCost: formData.vehicleCost,
        ...formData.expenses
      },
      currency: formData.currency,
      supplier: formData.supplier,
      expectedDelivery: formData.expectedDelivery,
      paymentMethod: formData.paymentMethod,
      notes: formData.notes
    };

    setVehicleOrders(vehicleOrders.map(order => 
      order.id === editingOrder.id ? updatedOrder : order
    ));
    setShowOrderForm(false);
    setEditingOrder(null);
  };

  const handleDeleteOrder = (orderId: string) => {
    if (confirm('Are you sure you want to delete this order?')) {
      setVehicleOrders(vehicleOrders.filter(order => order.id !== orderId));
    }
  };

  const handleUpdateStatus = (orderId: string, newStatus: 'ordered' | 'shipped' | 'clearing' | 'completed' | 'sold') => {
    if (newStatus === 'sold') {
      // If marking as sold, open the sale modal instead
      const order = vehicleOrders.find(o => o.id === orderId);
      if (order) {
        handleMarkAsSold(order);
      }
    } else {
      setVehicleOrders(vehicleOrders.map(order => 
        order.id === orderId ? { ...order, status: newStatus } : order
      ));
    }
  };

  const handleFormCancel = () => {
    setShowOrderForm(false);
    setEditingOrder(null);
  };

  const handleFormSubmit = (formData: VehicleOrderFormData) => {
    if (editingOrder) {
      handleUpdateOrder(formData);
    } else {
      handleNewOrder(formData);
    }
  };

  // Handle marking vehicle as sold
  const handleMarkAsSold = (order: VehicleOrder) => {
    setSaleModal(order);
  };

  const handleSaleSubmit = (saleData: {
    sellingPrice: number;
    customerName: string;
    customerContact: string;
    customerPaymentMethod: string;
    saleDate: string;
    notes?: string;
  }) => {
    if (!saleModal) return;

    const updatedOrder: VehicleOrder = {
      ...saleModal,
      status: 'sold',
      sellingPrice: saleData.sellingPrice,
      customerInfo: {
        name: saleData.customerName,
        contact: saleData.customerContact,
        paymentMethod: saleData.customerPaymentMethod,
      },
      saleDate: saleData.saleDate,
      notes: saleData.notes || saleModal.notes,
    };

    setVehicleOrders(vehicleOrders.map(order => 
      order.id === saleModal.id ? updatedOrder : order
    ));
    setSaleModal(null);
  };

  // Inventory handler functions
  const handleNewInventoryItem = (formData: InventoryFormData) => {
    const newItem: InventoryItem = {
      id: `INV${String(inventoryItems.length + 1).padStart(3, '0')}`,
      ...formData,
      status: 'available',
      dateAdded: new Date().toISOString().split('T')[0],
      lastUpdated: new Date().toISOString().split('T')[0],
    };

    setInventoryItems([...inventoryItems, newItem]);
    setShowInventoryForm(false);
  };

  const handleEditInventoryItem = (item: InventoryItem) => {
    setEditingInventoryItem(item);
    setShowInventoryForm(true);
  };

  const handleUpdateInventoryItem = (formData: InventoryFormData) => {
    if (!editingInventoryItem) return;

    const updatedItem: InventoryItem = {
      ...editingInventoryItem,
      ...formData,
      lastUpdated: new Date().toISOString().split('T')[0],
    };

    setInventoryItems(inventoryItems.map(item => 
      item.id === editingInventoryItem.id ? updatedItem : item
    ));
    setShowInventoryForm(false);
    setEditingInventoryItem(null);
  };

  const handleDeleteInventoryItem = (itemId: string) => {
    if (confirm('Are you sure you want to delete this inventory item?')) {
      setInventoryItems(inventoryItems.filter(item => item.id !== itemId));
    }
  };

  const handleInventoryStatusUpdate = (itemId: string, newStatus: 'available' | 'reserved' | 'sold' | 'maintenance') => {
    setInventoryItems(inventoryItems.map(item => 
      item.id === itemId ? { ...item, status: newStatus, lastUpdated: new Date().toISOString().split('T')[0] } : item
    ));
  };

  const handleInventoryFormCancel = () => {
    setShowInventoryForm(false);
    setEditingInventoryItem(null);
  };

  const handleInventoryFormSubmit = (formData: InventoryFormData) => {
    if (editingInventoryItem) {
      handleUpdateInventoryItem(formData);
    } else {
      handleNewInventoryItem(formData);
    }
  };

  // Filter orders based on search and status
  const filteredOrders = vehicleOrders.filter(order => {
    const matchesSearch = order.model.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         order.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         order.country.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (order.supplier && order.supplier.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = statusFilter === 'all' || order.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  // Filter inventory based on search, status, and condition
  const filteredInventory = inventoryItems.filter(item => {
    const matchesSearch = item.model.toLowerCase().includes(inventorySearchTerm.toLowerCase()) ||
                         item.brand.toLowerCase().includes(inventorySearchTerm.toLowerCase()) ||
                         item.id.toLowerCase().includes(inventorySearchTerm.toLowerCase()) ||
                         item.color.toLowerCase().includes(inventorySearchTerm.toLowerCase()) ||
                         (item.vin && item.vin.toLowerCase().includes(inventorySearchTerm.toLowerCase())) ||
                         (item.licensePlate && item.licensePlate.toLowerCase().includes(inventorySearchTerm.toLowerCase()));
    const matchesStatus = inventoryStatusFilter === 'all' || item.status === inventoryStatusFilter;
    const matchesCondition = inventoryConditionFilter === 'all' || item.condition === inventoryConditionFilter;
    return matchesSearch && matchesStatus && matchesCondition;
  });

  const getStatusBadge = (status: string) => {
    const statusMap = {
      ordered: 'badge-info',
      shipped: 'badge-warning', 
      clearing: 'badge-warning',
      completed: 'badge-success',
      sold: 'badge-success'
    };
    return statusMap[status as keyof typeof statusMap] || 'badge-info';
  };

  // Calculate real profit when selling price is available
  const calculateProfit = (order: VehicleOrder) => {
    if (order.sellingPrice && order.sellingPrice > 0) {
      return order.sellingPrice - order.totalCost;
    }
    return order.profit || 0;
  };

  // Calculate profit margin percentage
  const calculateProfitMargin = (order: VehicleOrder) => {
    if (order.sellingPrice && order.sellingPrice > 0) {
      const profit = calculateProfit(order);
      return (profit / order.sellingPrice) * 100;
    }
    return 0;
  };

  const formatCurrency = (amount: number, currency: string = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const totalAssets = vehicleOrders.reduce((sum, order) => sum + order.totalCost, 0);
  const totalProfit = vehicleOrders.reduce((sum, order) => sum + calculateProfit(order), 0);
  const completedOrders = vehicleOrders.filter(order => order.status === 'completed' || order.status === 'sold').length;
  const pendingOrders = vehicleOrders.filter(order => order.status !== 'completed' && order.status !== 'sold').length;
  const soldOrders = vehicleOrders.filter(order => order.status === 'sold').length;

  return (
    <div className="dashboard">
      {/* Key Metrics */}
      <div className="metrics-grid grid grid-4">
        <div className="card metric-card">
          <div className="metric-icon assets"></div>
          <div className="metric-info">
            <h3 className="metric-value">{formatCurrency(totalAssets)}</h3>
            <p className="metric-label">Total Assets</p>
          </div>
        </div>

        <div className="card metric-card">
          <div className="metric-icon profit"></div>
          <div className="metric-info">
            <h3 className="metric-value">{formatCurrency(totalProfit)}</h3>
            <p className="metric-label">Total Profit</p>
          </div>
        </div>

        <div className="card metric-card">
          <div className="metric-icon completed"></div>
          <div className="metric-info">
            <h3 className="metric-value">{completedOrders}</h3>
            <p className="metric-label">Completed Orders</p>
          </div>
        </div>

        <div className="card metric-card">
          <div className="metric-icon pending"></div>
          <div className="metric-info">
            <h3 className="metric-value">{pendingOrders}</h3>
            <p className="metric-label">Pending Orders</p>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => onTabChange('overview')}
        >
          Overview
        </button>
        <button 
          className={`tab-btn ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => onTabChange('orders')}
        >
          Vehicle Orders
        </button>
        <button 
          className={`tab-btn ${activeTab === 'expenses' ? 'active' : ''}`}
          onClick={() => onTabChange('expenses')}
        >
          Expense Tracking
        </button>
        <button 
          className={`tab-btn ${activeTab === 'reports' ? 'active' : ''}`}
          onClick={() => onTabChange('reports')}
        >
          Reports
        </button>
      </div>

      {/* Tab Content */}
      {activeTab === 'overview' && (
        <div className="tab-content">
          <div className="grid grid-2">
            <div className="card">
              <h3>Recent Orders</h3>
              <div className="order-list">
                {vehicleOrders.slice(0, 3).map(order => (
                  <div key={order.id} className="order-item">
                    <div className="order-details">
                      <h4>{order.model} ({order.year})</h4>
                      <p>Order ID: {order.id}</p>
                      <p>From: {order.country}</p>
                    </div>
                    <div className="order-status">
                      <span className={`badge ${getStatusBadge(order.status)}`}>
                        {order.status.toUpperCase()}
                      </span>
                      <p className="order-cost">{formatCurrency(order.totalCost, order.currency)}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="card">
              <h3>Quick Actions</h3>
              <div className="action-buttons">
                <button className="btn btn-primary" onClick={() => {setShowOrderForm(true); setEditingOrder(null);}}>New Vehicle Order</button>
                <button className="btn btn-secondary" onClick={() => onTabChange('expenses')}>Add Expense</button>
                <button className="btn btn-secondary" onClick={() => onTabChange('reports')}>Generate Report</button>
                <button className="btn btn-secondary" onClick={() => onTabChange('orders')}>View All Orders</button>
              </div>
            </div>
          </div>
        </div>
