import React, { useState } from 'react';
import './Dashboard.css';
import VehicleOrderForm from './VehicleOrderForm';

interface VehicleOrder {
  id: string;
  model: string;
  year: number;
  country: string;
  orderDate: string;
  status: 'ordered' | 'shipped' | 'clearing' | 'completed';
  totalCost: number;
  expenses: {
    vehicleCost: number;
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    customExpenses: { [key: string]: number };
  };
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  profit?: number;
  sellingPrice?: number;
  supplier?: string;
  expectedDelivery?: string;
  paymentMethod?: string;
  notes?: string;
  // Advanced fields
  customerInfo?: {
    name: string;
    contact: string;
    paymentMethod: string;
  };
  profitMargin?: number;
  saleDate?: string;
  marketValue?: number;
  depreciation?: number;
}

interface InventoryItem {
  id: string;
  model: string;
  brand: string;
  year: number;
  color: string;
  mileage: number;
  condition: 'excellent' | 'good' | 'fair' | 'needs_repair';
  purchasePrice: number;
  marketValue: number;
  sellingPrice?: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  location: string;
  status: 'available' | 'reserved' | 'sold' | 'maintenance';
  dateAdded: string;
  lastUpdated: string;
  features: string[];
  images?: string[];
  vin?: string;
  licensePlate?: string;
  fuelType: 'gasoline' | 'diesel' | 'hybrid' | 'electric';
  transmission: 'manual' | 'automatic' | 'cvt';
  engineSize?: string;
  bodyType: 'sedan' | 'suv' | 'hatchback' | 'coupe' | 'pickup' | 'van' | 'other';
  supplier?: string;
  purchaseDate?: string;
  warrantyExpiry?: string;
  notes?: string;
  reservedBy?: {
    name: string;
    contact: string;
    reservationDate: string;
    depositAmount?: number;
  };
  soldInfo?: {
    customerName: string;
    customerContact: string;
    saleDate: string;
    finalPrice: number;
    paymentMethod: string;
  };
}

interface InventoryFormData {
  model: string;
  brand: string;
  year: number;
  color: string;
  mileage: number;
  condition: 'excellent' | 'good' | 'fair' | 'needs_repair';
  purchasePrice: number;
  marketValue: number;
  sellingPrice?: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  location: string;
  features: string[];
  vin?: string;
  licensePlate?: string;
  fuelType: 'gasoline' | 'diesel' | 'hybrid' | 'electric';
  transmission: 'manual' | 'automatic' | 'cvt';
  engineSize?: string;
  bodyType: 'sedan' | 'suv' | 'hatchback' | 'coupe' | 'pickup' | 'van' | 'other';
  supplier?: string;
  purchaseDate?: string;
  warrantyExpiry?: string;
  notes?: string;
}

// Billing & POS Interfaces
interface Customer {
  id: string;
  name: string;
  contact: string;
  email?: string;
  address?: string;
  idNumber?: string;
  notes?: string;
}

interface PaymentRecord {
  id: string;
  transactionId: string;
  amount: number;
  paymentMethod: 'cash' | 'card' | 'bank_transfer' | 'check' | 'finance';
  paymentDate: string;
  receivedBy: string;
  notes?: string;
}

interface Transaction {
  id: string;
  type: 'reservation' | 'sale' | 'refund';
  customerId: string;
  inventoryId: string;
  vehicleDetails: {
    brand: string;
    model: string;
    year: number;
    color: string;
  };
  pricing: {
    vehiclePrice: number;
    taxes: number;
    fees: number;
    discount: number;
    totalAmount: number;
  };
  payments: PaymentRecord[];
  totalPaid: number;
  balanceRemaining: number;
  status: 'pending' | 'partial_paid' | 'fully_paid' | 'overdue' | 'cancelled' | 'completed';
  reservationDate?: string;
  expectedDelivery?: string;
  completionDate?: string;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  notes?: string;
  invoiceNumber?: string;
}

interface Invoice {
  id: string;
  invoiceNumber: string;
  transactionId: string;
  customerId: string;
  issueDate: string;
  dueDate: string;
  items: {
    description: string;
    quantity: number;
    unitPrice: number;
    total: number;
  }[];
  subtotal: number;
  taxRate: number;
  taxAmount: number;
  totalAmount: number;
  status: 'draft' | 'sent' | 'paid' | 'overdue' | 'cancelled';
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  notes?: string;
}

interface VehicleOrderFormData {
  model: string;
  year: number;
  country: string;
  supplier: string;
  vehicleCost: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  expectedDelivery: string;
  notes: string;
  expenses: {
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    customExpenses: { [key: string]: number };
  };
  paymentMethod: string;
}

interface DashboardProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ activeTab, onTabChange }) => {
  const [showOrderForm, setShowOrderForm] = useState(false);
