    const brandMap: { [key: string]: string } = {
      'toyota': 'Toyota',
      'camry': 'Toyota',
      'corolla': 'Toyota',
      'prius': 'Toyota',
      'honda': 'Honda',
      'accord': 'Honda',
      'civic': 'Honda',
      'cr-v': 'Honda',
