    // Create or find customer
    let customer = customers.find(c => c.contact === customerData.contact);
    if (!customer) {
      customer = {
        id: `CUST${String(customers.length + 1).padStart(3, '0')}`,
        ...customerData
      };
      setCustomers([...customers, customer]);
    }

    const vehicle = getInventoryById(inventoryId);
    if (!vehicle || !customer) return; // Add customer check here

    const newTransaction: Transaction = {
      id: `TXN${String(transactions.length + 1).padStart(3, '0')}`,
      type: 'reservation',
      customerId: customer.id,
      inventoryId,
      vehicleDetails: {
        brand: vehicle.brand,
        model: vehicle.model,
        year: vehicle.year,
        color: vehicle.color
      },
      pricing: {
        ...pricingData,
        totalAmount: pricingData.vehiclePrice + pricingData.taxes + pricingData.fees - pricingData.discount
