      currency: vehicle.currency,
      invoiceNumber: `INV-2024-${String(transactions.length + 1).padStart(3, '0')}`
    };

    setTransactions([...transactions, newTransaction]);

    // Update inventory status to reserved - customer is guaranteed to exist here
    const reservedByData = {
      name: customer.name,
      contact: customer.contact,
      reservationDate: newTransaction.reservationDate!,
    };

    setInventoryItems(items => items.map(item => 
      item.id === inventoryId 
