import React, { useState } from 'react';
import BankInvoice from './BankInvoice';
import CustomerInvoice from './CustomerInvoice';
import './Billing.css';

interface InvoiceFormData {
  // Customer Information
  customerName: string;
  customerAddress: string;
  customerContact: string;
  customerNIC: string;
  
  // Bank Information (for bank invoice)
  bankName: string;
  bankBranch: string;
  
  // Vehicle Details
  vehicleRegisteredNo: string;
  make: string;
  model: string;
  yearOfManufacture: number;
  chassisNo: string;
  engineNo: string;
  fuelType: string;
  colour: string;
  countryOfOrigin: string;
  
  // Financial Details
  vehicleCost: number;
  totalAmount: number;
  advanceAmount: number;
  loanAmount: number;
  balanceAmount: number;
  
  // Additional Expenses
  fuel: number;
  duty: number;
  driverCharge: number;
  clearanceCharge: number;
  demurrage: number;
  tax: number;
  
  // Payment Details
  paymentMethod: string;
  currency: string;
  
  // Seller Information
  sellerName: string;
  sellerContact: string;
  sellerNIC: string;
  
  // Invoice Details
  invoiceNumber: string;
  date: string;
}

const Billing: React.FC = () => {
  const [activeInvoiceType, setActiveInvoiceType] = useState<'customer' | 'bank'>('customer');
  const [showPreview, setShowPreview] = useState(false);
  const [formData, setFormData] = useState<InvoiceFormData>({
    customerName: '',
    customerAddress: '',
    customerContact: '',
    customerNIC: '',
    bankName: 'Hatton National Bank (HNB)',
    bankBranch: 'Kalawanchkudy',
    vehicleRegisteredNo: '',
    make: '',
    model: '',
    yearOfManufacture: new Date().getFullYear(),
    chassisNo: '',
    engineNo: '',
    fuelType: 'PETROL',
    colour: '',
    countryOfOrigin: 'JAPAN',
    vehicleCost: 0,
    totalAmount: 0,
    advanceAmount: 0,
    loanAmount: 0,
    balanceAmount: 0,
    fuel: 0,
    duty: 0,
    driverCharge: 0,
    clearanceCharge: 0,
    demurrage: 0,
    tax: 0,
    paymentMethod: 'Bank Transfer',
    currency: 'LKR',
    sellerName: 'Modern Car Sale',
    sellerContact: '067 22 29 174',
    sellerNIC: '198716101572',
    invoiceNumber: `INV-${Date.now()}`,
    date: new Date().toLocaleDateString('en-GB', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    })
  });

  // Sri Lankan banks list
  const sriLankanBanks = [
    'Bank of Ceylon (BOC)',
    'People\'s Bank',
    'Commercial Bank of Ceylon',
    'Hatton National Bank (HNB)',
    'Sampath Bank',
    'Nations Trust Bank',
    'DFCC Bank',
    'National Development Bank (NDB)',
    'Seylan Bank',
    'Union Bank',
    'Pan Asia Banking Corporation',
    'Cargills Bank',
    'HSBC Sri Lanka',
    'Standard Chartered Bank',
  ];

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => {
      const updated = {
        ...prev,
        [name]: ['yearOfManufacture', 'vehicleCost', 'totalAmount', 'advanceAmount', 'loanAmount', 'balanceAmount', 'fuel', 'duty', 'driverCharge', 'clearanceCharge', 'demurrage', 'tax'].includes(name)
          ? parseFloat(value) || 0
          : value
      };
      
      // Auto-calculate total amount
      if (['vehicleCost', 'fuel', 'duty', 'driverCharge', 'clearanceCharge', 'demurrage', 'tax'].includes(name)) {
        updated.totalAmount = updated.vehicleCost + updated.fuel + updated.duty + updated.driverCharge + 
                             updated.clearanceCharge + updated.demurrage + updated.tax;
      }
      
      // Auto-calculate loan/balance amounts
      if (name === 'advanceAmount' || name === 'totalAmount') {
        updated.loanAmount = updated.totalAmount - updated.advanceAmount;
        updated.balanceAmount = updated.totalAmount - updated.advanceAmount;
      }
      
      return updated;
    });
  };

  const handlePrint = () => {
    window.print();
  };

  const handleGenerateNew = () => {
    setFormData({
      ...formData,
      invoiceNumber: `INV-${Date.now()}`,
      date: new Date().toLocaleDateString('en-GB', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric'
      })
    });
    setShowPreview(false);
  };

  return (
    <div className="billing-container">
      <div className="billing-header">
        <h2> Invoice Management</h2>
        <p className="billing-subtitle">Generate invoices for customers and bank/leasing companies</p>
      </div>

      {!showPreview ? (
        <>
          {/* Invoice Type Selector */}
          <div className="invoice-type-selector">
            <button 
              className={`type-btn ${activeInvoiceType === 'customer' ? 'active' : ''}`}
              onClick={() => setActiveInvoiceType('customer')}
            >
               Customer Invoice
            </button>
            <button 
              className={`type-btn ${activeInvoiceType === 'bank' ? 'active' : ''}`}
              onClick={() => setActiveInvoiceType('bank')}
            >
               Bank/Leasing Invoice
            </button>
          </div>

          {/* Invoice Form */}
          <div className="invoice-form-container">
            <form className="invoice-form">
              {/* Invoice Details */}
              <div className="form-section">
                <h3>Invoice Details</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Invoice Number *</label>
                    <input
                      type="text"
                      name="invoiceNumber"
                      value={formData.invoiceNumber}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Date *</label>
                    <input
                      type="text"
                      name="date"
                      value={formData.date}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="DD/MM/YYYY"
                      required
                    />
                  </div>
                </div>
              </div>

              {/* Customer Information */}
              <div className="form-section">
                <h3>Customer Information</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Customer Name *</label>
                    <input
                      type="text"
                      name="customerName"
                      value={formData.customerName}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Contact Number *</label>
                    <input
                      type="text"
                      name="customerContact"
                      value={formData.customerContact}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    />
                  </div>
                  <div className="form-group full-width">
                    <label>Address *</label>
                    <textarea
                      name="customerAddress"
                      value={formData.customerAddress}
                      onChange={handleInputChange}
                      className="form-input"
                      rows={2}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>NIC Number *</label>
                    <input
                      type="text"
                      name="customerNIC"
                      value={formData.customerNIC}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    />
                  </div>
                </div>
              </div>

              {/* Bank Information (only for bank invoice) */}
              {activeInvoiceType === 'bank' && (
                <div className="form-section">
                  <h3> Bank/Leasing Company Information</h3>
                  <div className="form-grid">
                    <div className="form-group">
                      <label>Bank Name *</label>
                      <select
                        name="bankName"
                        value={formData.bankName}
                        onChange={handleInputChange}
                        className="form-input"
                        required
                      >
                        {sriLankanBanks.map(bank => (
                          <option key={bank} value={bank}>{bank}</option>
                        ))}
                      </select>
                    </div>
                    <div className="form-group">
                      <label>Branch *</label>
                      <input
                        type="text"
                        name="bankBranch"
                        value={formData.bankBranch}
                        onChange={handleInputChange}
                        className="form-input"
                        required
                      />
                    </div>
                  </div>
                </div>
              )}

              {/* Vehicle Details */}
              <div className="form-section">
                <h3> Vehicle Details</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Vehicle Registration No</label>
                    <input
                      type="text"
                      name="vehicleRegisteredNo"
                      value={formData.vehicleRegisteredNo}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="Leave empty for UNREGISTER"
                    />
                  </div>
                  <div className="form-group">
                    <label>Make *</label>
                    <input
                      type="text"
                      name="make"
                      value={formData.make}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="e.g., DAIHATSU"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Model *</label>
                    <input
                      type="text"
                      name="model"
                      value={formData.model}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="e.g., 5BA-LA350S"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Year of Manufacture *</label>
                    <input
                      type="number"
                      name="yearOfManufacture"
                      value={formData.yearOfManufacture}
                      onChange={handleInputChange}
                      className="form-input"
                      min="1990"
                      max="2030"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Chassis NO *</label>
                    <input
                      type="text"
                      name="chassisNo"
                      value={formData.chassisNo}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="e.g., LA350S-0425102"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Engine No *</label>
                    <input
                      type="text"
                      name="engineNo"
                      value={formData.engineNo}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="e.g., KF-K469892"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Fuel Type *</label>
                    <select
                      name="fuelType"
                      value={formData.fuelType}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    >
                      <option value="PETROL">PETROL</option>
                      <option value="DIESEL">DIESEL</option>
                      <option value="HYBRID">HYBRID</option>
                      <option value="ELECTRIC">ELECTRIC</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Colour *</label>
                    <input
                      type="text"
                      name="colour"
                      value={formData.colour}
                      onChange={handleInputChange}
                      className="form-input"
                      placeholder="e.g., RED"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Country of Origin *</label>
                    <select
                      name="countryOfOrigin"
                      value={formData.countryOfOrigin}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    >
                      <option value="JAPAN">JAPAN</option>
                      <option value="GERMANY">GERMANY</option>
                      <option value="USA">USA</option>
                      <option value="UK">UK</option>
                      <option value="SOUTH KOREA">SOUTH KOREA</option>
                      <option value="INDIA">INDIA</option>
                    </select>
                  </div>
                </div>
              </div>

              {/* Financial Details */}
              <div className="form-section">
                <h3> Financial Details</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Currency *</label>
                    <select
                      name="currency"
                      value={formData.currency}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    >
                      <option value="LKR">LKR (Rs.)</option>
                      <option value="USD">USD ($)</option>
                      <option value="EUR">EUR ()</option>
                      <option value="JPY">JPY ()</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Vehicle Cost *</label>
                    <input
                      type="number"
                      name="vehicleCost"
                      value={formData.vehicleCost}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                      required
                    />
                  </div>
                </div>

                <h4 style={{marginTop: '1rem', marginBottom: '0.5rem'}}>Additional Expenses</h4>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Fuel</label>
                    <input
                      type="number"
                      name="fuel"
                      value={formData.fuel}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                    />
                  </div>
                  <div className="form-group">
                    <label>Duty</label>
                    <input
                      type="number"
                      name="duty"
                      value={formData.duty}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                    />
                  </div>
                  <div className="form-group">
                    <label>Driver Charge</label>
                    <input
                      type="number"
                      name="driverCharge"
                      value={formData.driverCharge}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                    />
                  </div>
                  <div className="form-group">
                    <label>Clearance Charge</label>
                    <input
                      type="number"
                      name="clearanceCharge"
                      value={formData.clearanceCharge}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                    />
                  </div>
                  <div className="form-group">
                    <label>Demurrage</label>
                    <input
                      type="number"
                      name="demurrage"
                      value={formData.demurrage}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                    />
                  </div>
                  <div className="form-group">
                    <label>Tax</label>
                    <input
                      type="number"
                      name="tax"
                      value={formData.tax}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>

                <div className="form-grid" style={{marginTop: '1rem'}}>
                  <div className="form-group highlight-field">
                    <label>Total Amount *</label>
                    <input
                      type="number"
                      name="totalAmount"
                      value={formData.totalAmount}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                      required
                      readOnly
                      style={{background: '#f8f9fa', fontWeight: 'bold'}}
                    />
                  </div>
                  <div className="form-group">
                    <label>Advance Amount *</label>
                    <input
                      type="number"
                      name="advanceAmount"
                      value={formData.advanceAmount}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                      required
                    />
                  </div>
                  <div className="form-group highlight-field">
                    <label>{activeInvoiceType === 'bank' ? 'Loan Amount' : 'Balance Amount'} *</label>
                    <input
                      type="number"
                      name={activeInvoiceType === 'bank' ? 'loanAmount' : 'balanceAmount'}
                      value={activeInvoiceType === 'bank' ? formData.loanAmount : formData.balanceAmount}
                      onChange={handleInputChange}
                      className="form-input"
                      min="0"
                      step="0.01"
                      required
                      readOnly
                      style={{background: '#fff3cd', fontWeight: 'bold'}}
                    />
                  </div>
                  <div className="form-group">
                    <label>Payment Method *</label>
                    <select
                      name="paymentMethod"
                      value={formData.paymentMethod}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    >
                      <option value="Bank Transfer">Bank Transfer</option>
                      <option value="Cash">Cash</option>
                      <option value="Cheque">Cheque</option>
                      <option value="Card Payment">Card Payment</option>
                      <option value="Leasing">Leasing</option>
                    </select>
                  </div>
                </div>
              </div>

              {/* Seller Information */}
              <div className="form-section">
                <h3> Seller Information</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Seller Name *</label>
                    <input
                      type="text"
                      name="sellerName"
                      value={formData.sellerName}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Seller Contact *</label>
                    <input
                      type="text"
                      name="sellerContact"
                      value={formData.sellerContact}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Seller NIC *</label>
                    <input
                      type="text"
                      name="sellerNIC"
                      value={formData.sellerNIC}
                      onChange={handleInputChange}
                      className="form-input"
                      required
                    />
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="form-actions">
                <button 
                  type="button" 
                  className="btn btn-primary"
                  onClick={() => setShowPreview(true)}
                >
                   Preview Invoice
                </button>
              </div>
            </form>
          </div>
        </>
      ) : (
        <>
          {/* Invoice Preview */}
          <div className="invoice-preview-container">
            <div className="preview-actions no-print">
              <button className="btn btn-secondary" onClick={() => setShowPreview(false)}>
                 Back to Form
              </button>
              <button className="btn btn-success" onClick={handlePrint}>
                 Print Invoice
              </button>
              <button className="btn btn-primary" onClick={handleGenerateNew}>
                 Generate New Invoice
              </button>
            </div>

            {activeInvoiceType === 'customer' ? (
              <CustomerInvoice 
                invoiceData={{
                  invoiceNumber: formData.invoiceNumber,
                  date: formData.date,
                  customerName: formData.customerName,
                  customerAddress: formData.customerAddress,
                  customerContact: formData.customerContact,
                  customerNIC: formData.customerNIC,
                  vehicleRegisteredNo: formData.vehicleRegisteredNo,
                  make: formData.make,
                  model: formData.model,
                  yearOfManufacture: formData.yearOfManufacture,
                  chassisNo: formData.chassisNo,
                  engineNo: formData.engineNo,
                  fuelType: formData.fuelType,
                  colour: formData.colour,
                  countryOfOrigin: formData.countryOfOrigin,
                  vehicleCost: formData.vehicleCost,
                  additionalExpenses: {
                    fuel: formData.fuel,
                    duty: formData.duty,
                    driverCharge: formData.driverCharge,
                    clearanceCharge: formData.clearanceCharge,
                    demurrage: formData.demurrage,
                    tax: formData.tax,
                  },
                  totalAmount: formData.totalAmount,
                  advanceAmount: formData.advanceAmount,
                  balanceAmount: formData.balanceAmount,
                  paymentMethod: formData.paymentMethod,
                  sellerName: formData.sellerName,
                  sellerContact: formData.sellerContact,
                  sellerNIC: formData.sellerNIC,
                  currency: formData.currency,
                }}
              />
            ) : (
              <BankInvoice 
                invoiceData={{
                  invoiceNumber: formData.invoiceNumber,
                  date: formData.date,
                  bankName: formData.bankName,
                  bankBranch: formData.bankBranch,
                  vehicleRegisteredNo: formData.vehicleRegisteredNo,
                  make: formData.make,
                  model: formData.model,
                  yearOfManufacture: formData.yearOfManufacture,
                  chassisNo: formData.chassisNo,
                  engineNo: formData.engineNo,
                  fuelType: formData.fuelType,
                  colour: formData.colour,
                  countryOfOrigin: formData.countryOfOrigin,
                  totalAmount: formData.totalAmount,
                  advanceAmount: formData.advanceAmount,
                  loanAmount: formData.loanAmount,
                  deliverToName: formData.customerName,
                  deliverToAddress: formData.customerAddress,
                  deliverToNIC: formData.customerNIC,
                  sellerName: formData.sellerName,
                  sellerContact: formData.sellerContact,
                  sellerNIC: formData.sellerNIC,
                }}
              />
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Billing;
