// Transaction Form Component
interface TransactionFormProps {
  inventoryItems: InventoryItem[];
  customers: Customer[];
  onSubmit: (inventoryId: string, customerData: {
    name: string;
    contact: string;
    email?: string;
    address?: string;
    idNumber?: string;
  }, pricingData: {
    vehiclePrice: number;
    taxes: number;
    fees: number;
    discount: number;
  }) => void;
  onCancel: () => void;
}

const TransactionForm: React.FC<TransactionFormProps> = ({ inventoryItems, customers, onSubmit, onCancel }) => {
  const [selectedVehicle, setSelectedVehicle] = useState('');
  const [customerData, setCustomerData] = useState({
    name: '',
    contact: '',
    email: '',
    address: '',
    idNumber: ''
  });
  const [pricingData, setPricingData] = useState({
    vehiclePrice: 0,
    taxes: 0,
    fees: 500, // Default processing fee
    discount: 0
  });

  const handleVehicleSelect = (vehicleId: string) => {
    const vehicle = inventoryItems.find(v => v.id === vehicleId);
    if (vehicle) {
      setSelectedVehicle(vehicleId);
      setPricingData(prev => ({
        ...prev,
        vehiclePrice: vehicle.sellingPrice || vehicle.marketValue,
        taxes: Math.round((vehicle.sellingPrice || vehicle.marketValue) * 0.05) // 5% tax assumption
      }));
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedVehicle || !customerData.name || !customerData.contact) {
      alert('Please fill in all required fields');
      return;
    }
    onSubmit(selectedVehicle, customerData, pricingData);
  };

  const getTotalAmount = () => {
    return pricingData.vehiclePrice + pricingData.taxes + pricingData.fees - pricingData.discount;
  };

  return (
    <div className="vehicle-order-form-overlay">
      <div className="vehicle-order-form">
        <div className="form-header">
          <h2>ðŸ’° New Transaction</h2>
          <button className="close-btn" onClick={onCancel}>Ã—</button>
