import React, { useState } from 'react';
import './Dashboard.css';
import VehicleOrderForm from './VehicleOrderForm';

interface VehicleOrder {
  id: string;
  model: string;
  year: number;
  country: string;
  orderDate: string;
  status: 'ordered' | 'shipped' | 'clearing' | 'completed' | 'sold';
  totalCost: number;
  expenses: {
    vehicleCost: number;
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    cno: number;
    cif: number;
  };
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  profit?: number;
  sellingPrice?: number;
  supplier?: string;
  expectedDelivery?: string;
  paymentMethod?: string;
  notes?: string;
  // Advanced fields
  customerInfo?: {
    name: string;
    contact: string;
    paymentMethod: string;
  };
  profitMargin?: number;
  saleDate?: string;
  marketValue?: number;
  depreciation?: number;
}

interface VehicleOrderFormData {
  model: string;
  year: number;
  country: string;
  supplier: string;
  vehicleCost: number;
  currency: 'USD' | 'JPY' | 'EUR' | 'GBP';
  expectedDelivery: string;
  notes: string;
  expenses: {
    fuel: number;
    duty: number;
    driverCharge: number;
    clearanceCharge: number;
    demurrage: number;
    tax: number;
    cno: number;
    cif: number;
  };
  paymentMethod: string;
}

interface DashboardProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

const Dashboard: React.FC<DashboardProps> = ({ activeTab, onTabChange }) => {
  const [showOrderForm, setShowOrderForm] = useState(false);
  const [editingOrder, setEditingOrder] = useState<VehicleOrder | null>(null);
  const [orderDetailsModal, setOrderDetailsModal] = useState<VehicleOrder | null>(null);
  const [saleModal, setSaleModal] = useState<VehicleOrder | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  
  // Sample data
  const [vehicleOrders, setVehicleOrders] = useState<VehicleOrder[]>([
    {
      id: 'ORD001',
      model: 'Toyota Camry',
      year: 2023,
      country: 'Japan',
      orderDate: '2024-01-15',
      status: 'sold',
      totalCost: 25000,
      expenses: {
        vehicleCost: 20000,
        fuel: 500,
        duty: 2000,
        driverCharge: 800,
        clearanceCharge: 600,
        demurrage: 300,
        tax: 600,
        cno: 100,
        cif: 100
      },
      currency: 'USD',
      sellingPrice: 32000,
      supplier: 'Tokyo Auto Exports',
      expectedDelivery: '2024-03-15',
      paymentMethod: 'Bank Transfer',
      notes: 'Low mileage, excellent condition',
      customerInfo: {
        name: 'Ahmed Hassan',
        contact: '+971-50-123-4567',
        paymentMethod: 'Bank Transfer'
      },
      saleDate: '2024-04-10',
      marketValue: 31000
    },
    {
      id: 'ORD002',
      model: 'Honda Accord',
      year: 2023,
      country: 'Japan',
      orderDate: '2024-02-10',
      status: 'clearing',
      totalCost: 23000,
      expenses: {
        vehicleCost: 18000,
        fuel: 450,
        duty: 1800,
        driverCharge: 750,
        clearanceCharge: 550,
        demurrage: 250,
        tax: 550,
        cno: 100,
        cif: 550
      },
      currency: 'USD',
      supplier: 'Osaka Motors Ltd',
      expectedDelivery: '2024-04-20',
      paymentMethod: 'Letter of Credit',
      notes: 'Premium package, navigation system included'
    },
    {
      id: 'ORD003',
      model: 'BMW X5',
      year: 2022,
      country: 'Germany',
      orderDate: '2024-01-25',
      status: 'shipped',
      totalCost: 45000,
      expenses: {
        vehicleCost: 38000,
        fuel: 800,
        duty: 3500,
        driverCharge: 1200,
        clearanceCharge: 900,
        demurrage: 0,
        tax: 1100,
        cno: 150,
        cif: 350
      },
      currency: 'EUR',
      supplier: 'Munich Auto GmbH',
      expectedDelivery: '2024-03-30',
      paymentMethod: 'Bank Transfer',
      notes: 'Luxury SUV, full options'
    },
    {
      id: 'ORD004',
      model: 'Nissan GTR',
      year: 2024,
      country: 'Japan',
      orderDate: '2024-03-01',
      status: 'ordered',
      totalCost: 85000,
      expenses: {
        vehicleCost: 75000,
        fuel: 600,
        duty: 5000,
        driverCharge: 1000,
        clearanceCharge: 800,
        demurrage: 0,
        tax: 2400,
        cno: 100,
        cif: 100
      },
      currency: 'JPY',
      supplier: 'Yokohama Sport Cars',
      expectedDelivery: '2024-05-15',
      paymentMethod: 'Bank Transfer',
      notes: 'Performance sports car, track package'
    }
  ]);

  // Handler functions
  const handleNewOrder = (formData: VehicleOrderFormData) => {
    const newOrder: VehicleOrder = {
      id: `ORD${String(vehicleOrders.length + 1).padStart(3, '0')}`,
      model: formData.model,
      year: formData.year,
      country: formData.country,
      orderDate: new Date().toISOString().split('T')[0],
      status: 'ordered',
      totalCost: formData.vehicleCost + Object.values(formData.expenses).reduce((sum, val) => sum + val, 0),
      expenses: {
        vehicleCost: formData.vehicleCost,
        ...formData.expenses
      },
      currency: formData.currency,
      supplier: formData.supplier,
      expectedDelivery: formData.expectedDelivery,
      paymentMethod: formData.paymentMethod,
      notes: formData.notes
    };
    
    setVehicleOrders([...vehicleOrders, newOrder]);
    setShowOrderForm(false);
    onTabChange('orders');
  };

  const handleEditOrder = (order: VehicleOrder) => {
    setEditingOrder(order);
    setShowOrderForm(true);
  };

  const handleUpdateOrder = (formData: VehicleOrderFormData) => {
    if (!editingOrder) return;
    
    const updatedOrder: VehicleOrder = {
      ...editingOrder,
      model: formData.model,
      year: formData.year,
      country: formData.country,
      totalCost: formData.vehicleCost + Object.values(formData.expenses).reduce((sum, val) => sum + val, 0),
      expenses: {
        vehicleCost: formData.vehicleCost,
        ...formData.expenses
      },
      currency: formData.currency,
      supplier: formData.supplier,
      expectedDelivery: formData.expectedDelivery,
      paymentMethod: formData.paymentMethod,
      notes: formData.notes
    };
    
    setVehicleOrders(vehicleOrders.map(order => 
      order.id === editingOrder.id ? updatedOrder : order
    ));
    setShowOrderForm(false);
    setEditingOrder(null);
  };

  const handleDeleteOrder = (orderId: string) => {
    if (confirm('Are you sure you want to delete this order?')) {
      setVehicleOrders(vehicleOrders.filter(order => order.id !== orderId));
    }
  };

  const handleUpdateStatus = (orderId: string, newStatus: 'ordered' | 'shipped' | 'clearing' | 'completed') => {
    setVehicleOrders(vehicleOrders.map(order => 
      order.id === orderId ? { ...order, status: newStatus } : order
    ));
  };

  const handleFormCancel = () => {
    setShowOrderForm(false);
    setEditingOrder(null);
  };

  const handleFormSubmit = (formData: VehicleOrderFormData) => {
    if (editingOrder) {
      handleUpdateOrder(formData);
    } else {
      handleNewOrder(formData);
    }
  };

  // Handle marking vehicle as sold
  const handleMarkAsSold = (order: VehicleOrder) => {
    setSaleModal(order);
  };

  const handleSaleSubmit = (saleData: {
    sellingPrice: number;
    customerName: string;
    customerContact: string;
    customerPaymentMethod: string;
    saleDate: string;
    notes?: string;
  }) => {
    if (!saleModal) return;
    
    const updatedOrder: VehicleOrder = {
      ...saleModal,
      status: 'sold',
      sellingPrice: saleData.sellingPrice,
      customerInfo: {
        name: saleData.customerName,
        contact: saleData.customerContact,
        paymentMethod: saleData.customerPaymentMethod,
      },
      saleDate: saleData.saleDate,
      notes: saleData.notes || saleModal.notes,
    };
    
    setVehicleOrders(vehicleOrders.map(order => 
      order.id === saleModal.id ? updatedOrder : order
    ));
    setSaleModal(null);
  };

  // Filter orders based on search and status
  const filteredOrders = vehicleOrders.filter(order => {
    const matchesSearch = order.model.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         order.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         order.country.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (order.supplier && order.supplier.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = statusFilter === 'all' || order.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const getStatusBadge = (status: string) => {
    const statusMap = {
      ordered: 'badge-info',
      shipped: 'badge-warning', 
      clearing: 'badge-warning',
      completed: 'badge-success',
      sold: 'badge-success'
    };
    return statusMap[status as keyof typeof statusMap] || 'badge-info';
  };

  // Calculate real profit when selling price is available
  const calculateProfit = (order: VehicleOrder) => {
    if (order.sellingPrice && order.sellingPrice > 0) {
      return order.sellingPrice - order.totalCost;
    }
    return order.profit || 0;
  };

  // Calculate profit margin percentage
  const calculateProfitMargin = (order: VehicleOrder) => {
    if (order.sellingPrice && order.sellingPrice > 0) {
      const profit = calculateProfit(order);
      return (profit / order.sellingPrice) * 100;
    }
    return 0;
  };

  const formatCurrency = (amount: number, currency: string = 'USD') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
    }).format(amount);
  };

  const totalAssets = vehicleOrders.reduce((sum, order) => sum + order.totalCost, 0);
  const totalProfit = vehicleOrders.reduce((sum, order) => sum + calculateProfit(order), 0);
  const completedOrders = vehicleOrders.filter(order => order.status === 'completed' || order.status === 'sold').length;
  const pendingOrders = vehicleOrders.filter(order => order.status !== 'completed' && order.status !== 'sold').length;
  const soldOrders = vehicleOrders.filter(order => order.status === 'sold').length;

  return (
    <div className="dashboard">
      {/* Key Metrics */}
      <div className="metrics-grid grid grid-4">
        <div className="card metric-card">
          <div className="metric-icon assets"></div>
          <div className="metric-info">
            <h3 className="metric-value">{formatCurrency(totalAssets)}</h3>
            <p className="metric-label">Total Assets</p>
          </div>
        </div>
        
        <div className="card metric-card">
          <div className="metric-icon profit"></div>
          <div className="metric-info">
            <h3 className="metric-value">{formatCurrency(totalProfit)}</h3>
            <p className="metric-label">Total Profit</p>
          </div>
        </div>
        
        <div className="card metric-card">
          <div className="metric-icon completed"></div>
          <div className="metric-info">
            <h3 className="metric-value">{completedOrders}</h3>
            <p className="metric-label">Completed Orders</p>
          </div>
        </div>
        
        <div className="card metric-card">
          <div className="metric-icon pending"></div>
          <div className="metric-info">
            <h3 className="metric-value">{pendingOrders}</h3>
            <p className="metric-label">Pending Orders</p>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => onTabChange('overview')}
        >
          Overview
        </button>
        <button 
          className={`tab-btn ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => onTabChange('orders')}
        >
          Vehicle Orders
        </button>
        <button 
          className={`tab-btn ${activeTab === 'expenses' ? 'active' : ''}`}
          onClick={() => onTabChange('expenses')}
        >
          Expense Tracking
        </button>
        <button 
          className={`tab-btn ${activeTab === 'reports' ? 'active' : ''}`}
          onClick={() => onTabChange('reports')}
        >
          Reports
        </button>
      </div>

      {/* Tab Content */}
      {activeTab === 'overview' && (
        <div className="tab-content">
          <div className="grid grid-2">
            <div className="card">
              <h3>Recent Orders</h3>
              <div className="order-list">
                {vehicleOrders.slice(0, 3).map(order => (
                  <div key={order.id} className="order-item">
                    <div className="order-details">
                      <h4>{order.model} ({order.year})</h4>
                      <p>Order ID: {order.id}</p>
                      <p>From: {order.country}</p>
                    </div>
                    <div className="order-status">
                      <span className={`badge ${getStatusBadge(order.status)}`}>
                        {order.status.toUpperCase()}
                      </span>
                      <p className="order-cost">{formatCurrency(order.totalCost, order.currency)}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            
            <div className="card">
              <h3>Quick Actions</h3>
              <div className="action-buttons">
                <button className="btn btn-primary" onClick={() => {setShowOrderForm(true); setEditingOrder(null);}}>New Vehicle Order</button>
                <button className="btn btn-secondary" onClick={() => onTabChange('expenses')}>Add Expense</button>
                <button className="btn btn-secondary" onClick={() => onTabChange('reports')}>Generate Report</button>
                <button className="btn btn-secondary" onClick={() => onTabChange('orders')}>View All Orders</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'orders' && (
        <div className="tab-content">
          <div className="card">
            <div className="orders-header">
              <h3>Vehicle Orders Management</h3>
              <div className="orders-controls">
                <div className="search-filter-group">
                  <input
                    type="text"
                    placeholder="Search orders..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                  />
                  <select
                    value={statusFilter}
                    onChange={(e) => setStatusFilter(e.target.value)}
                    className="status-filter"
                  >
                    <option value="all">All Status</option>
                    <option value="ordered">Ordered</option>
                    <option value="shipped">Shipped</option>
                    <option value="clearing">Clearing</option>
                    <option value="completed">Completed</option>
                    <option value="sold">Sold</option>
                  </select>
                </div>
                <button 
                  className="btn btn-primary" 
                  onClick={() => {setShowOrderForm(true); setEditingOrder(null);}}
                >
                  Add New Order
                </button>
              </div>
            </div>
            
            {filteredOrders.length > 0 ? (
              <div className="orders-table-container">
                <table className="table orders-table">
                  <thead>
                    <tr>
                      <th>Order ID</th>
                      <th>Vehicle</th>
                      <th>Supplier</th>
                      <th>Country</th>
                      <th>Order Date</th>
                      <th>Expected Delivery</th>
                      <th>Status</th>
                      <th>Total Cost</th>
                      <th>Payment Method</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredOrders.map(order => (
                      <tr key={order.id}>
                        <td data-label="Order ID">
                          <strong>{order.id}</strong>
                        </td>
                        <td data-label="Vehicle">
                          <div className="vehicle-info">
                            <strong>{order.model}</strong>
                            <small>({order.year})</small>
                          </div>
                        </td>
                        <td data-label="Supplier">
                          {order.supplier || 'N/A'}
                        </td>
                        <td data-label="Country">
                          <div className="country-flag">
                             {order.country}
                          </div>
                        </td>
                        <td data-label="Order Date">
                          {order.orderDate}
                        </td>
                        <td data-label="Expected Delivery">
                          {order.expectedDelivery || 'TBD'}
                        </td>
                        <td data-label="Status">
                          <div className="status-cell">
                            <span className={`badge ${getStatusBadge(order.status)}`}>
                              {order.status.toUpperCase()}
                            </span>
                            <select
                              value={order.status}
                              onChange={(e) => handleUpdateStatus(order.id, e.target.value as any)}
                              className="status-update-select"
                            >
                              <option value="ordered">Ordered</option>
                              <option value="shipped">Shipped</option>
                              <option value="clearing">Clearing</option>
                              <option value="completed">Completed</option>
                            </select>
                          </div>
                        </td>
                        <td data-label="Total Cost">
                          <div className="cost-display">
                            <strong>{formatCurrency(order.totalCost, order.currency)}</strong>
                            {order.sellingPrice && (
                              <div className="selling-info">
                                <small className="selling-price">
                                  Selling: {formatCurrency(order.sellingPrice, order.currency)}
                                </small>
                                <small className="profit-indicator">
                                  Profit: {formatCurrency(calculateProfit(order), order.currency)} 
                                  ({calculateProfitMargin(order).toFixed(1)}%)
                                </small>
                              </div>
                            )}
                          </div>
                        </td>
                        <td data-label="Payment Method">
                          {order.paymentMethod || 'N/A'}
                        </td>
                        <td data-label="Actions">
                          <div className="action-buttons-cell">
                            <button 
                              className="btn btn-info btn-sm" 
                              onClick={() => setOrderDetailsModal(order)}
                              title="View Details"
                            >
                              
                            </button>
                            <button 
                              className="btn btn-secondary btn-sm" 
                              onClick={() => handleEditOrder(order)}
                              title="Edit Order"
                            >
                              
                            </button>
                            {(order.status === 'completed' || order.status === 'clearing') && order.status !== 'sold' && (
                              <button 
                                className="btn btn-success btn-sm" 
                                onClick={() => handleMarkAsSold(order)}
                                title="Mark as Sold"
                              >
                                
                              </button>
                            )}
                            <button 
                              className="btn btn-danger btn-sm" 
                              onClick={() => handleDeleteOrder(order.id)}
                              title="Delete Order"
                            >
                              
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="no-orders">
                <h4>No orders found</h4>
                <p>
                  {searchTerm || statusFilter !== 'all' 
                    ? 'No orders match your current filters.' 
                    : 'No vehicle orders yet. Create your first order to get started!'
                  }
                </p>
                <button 
                  className="btn btn-primary" 
                  onClick={() => {setShowOrderForm(true); setEditingOrder(null);}}
                >
                  Create First Order
                </button>
              </div>
            )}
          </div>
          
          {/* Order Summary Stats */}
          <div className="grid grid-4" style={{marginTop: '2rem'}}>
            <div className="card metric-card">
              <div className="metric-icon orders"></div>
              <div className="metric-info">
                <h3 className="metric-value">{filteredOrders.length}</h3>
                <p className="metric-label">
                  {searchTerm || statusFilter !== 'all' ? 'Filtered Orders' : 'Total Orders'}
                </p>
              </div>
            </div>
            
            <div className="card metric-card">
              <div className="metric-icon in-transit"></div>
              <div className="metric-info">
                <h3 className="metric-value">
                  {filteredOrders.filter(o => o.status === 'shipped').length}
                </h3>
                <p className="metric-label">In Transit</p>
              </div>
            </div>
            
            <div className="card metric-card">
              <div className="metric-icon clearing"></div>
              <div className="metric-info">
                <h3 className="metric-value">
                  {filteredOrders.filter(o => o.status === 'clearing').length}
                </h3>
                <p className="metric-label">In Clearing</p>
              </div>
            </div>
            
            <div className="card metric-card">
              <div className="metric-icon total-value"></div>
              <div className="metric-info">
                <h3 className="metric-value">
                  {formatCurrency(
                    filteredOrders.reduce((sum, order) => sum + order.totalCost, 0)
                  )}
                </h3>
                <p className="metric-label">Total Value</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'expenses' && (
        <div className="tab-content">
          <div className="card">
            <h3>Expense Breakdown</h3>
            <div className="expense-categories grid grid-3">
              {vehicleOrders[0] && Object.entries(vehicleOrders[0].expenses).map(([key, value]) => (
                <div key={key} className="expense-item">
                  <h4>{key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}</h4>
                  <p className="expense-amount">{formatCurrency(value)}</p>
                </div>
              ))}
            </div>
          </div>
          
          <div className="grid grid-2">
            <div className="card">
              <h3>CNO & CIF Tracking</h3>
              <div className="document-tracking">
                <div className="doc-item">
                  <span className="doc-label">CNO Status:</span>
                  <span className="badge badge-success">Approved</span>
                </div>
                <div className="doc-item">
                  <span className="doc-label">CIF Value:</span>
                  <span className="doc-value">{formatCurrency(550)}</span>
                </div>
              </div>
            </div>
            
            <div className="card">
              <h3>Payment Methods</h3>
              <div className="payment-methods">
                <div className="payment-item">
                  <span className="payment-label">Demurrage Payment:</span>
                  <span className="payment-method">Bank Transfer</span>
                </div>
                <div className="payment-item">
                  <span className="payment-label">Currency Status:</span>
                  <span className="badge badge-info">USD</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'reports' && (
        <div className="tab-content">
          <div className="grid grid-2">
            <div className="card">
              <h3>Monthly Reports</h3>
              <div className="report-list">
                <div className="report-item">
                  <h4>September 2024</h4>
                  <p>Total Sales: {formatCurrency(48000)}</p>
                  <p>Total Profit: {formatCurrency(5000)}</p>
                  <button className="btn btn-secondary btn-sm">Download PDF</button>
                </div>
                <div className="report-item">
                  <h4>August 2024</h4>
                  <p>Total Sales: {formatCurrency(35000)}</p>
                  <p>Total Profit: {formatCurrency(3500)}</p>
                  <button className="btn btn-secondary btn-sm">Download PDF</button>
                </div>
              </div>
            </div>
            
            <div className="card">
              <h3>Analytics Summary</h3>
              <div className="analytics">
                <div className="analytics-item">
                  <span className="analytics-label">Average Profit Margin:</span>
                  <span className="analytics-value">20.8%</span>
                </div>
                <div className="analytics-item">
                  <span className="analytics-label">Best Performing Country:</span>
                  <span className="analytics-value">Japan</span>
                </div>
                <div className="analytics-item">
                  <span className="analytics-label">Total Vehicles in Stock:</span>
                  <span className="analytics-value">12</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'inventory' && (
        <div className="tab-content">
          <div className="card">
            <h3>Inventory Management</h3>
            <p>Inventory management features coming soon...</p>
            <div className="grid grid-2" style={{marginTop: '2rem'}}>
              <div className="card">
                <h4>Current Stock</h4>
                <p>12 vehicles in stock</p>
              </div>
              <div className="card">
                <h4>Total Value</h4>
                <p>{formatCurrency(vehicleOrders.reduce((sum, order) => sum + order.totalCost, 0))}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'settings' && (
        <div className="tab-content">
          <div className="card">
            <h3>System Settings</h3>
            <p>Settings panel coming soon...</p>
            <div style={{marginTop: '2rem'}}>
              <div className="form-group">
                <label className="form-label">Default Currency</label>
                <select className="form-input" style={{maxWidth: '200px'}}>
                  <option value="USD">USD ($)</option>
                  <option value="JPY">JPY ()</option>
                  <option value="EUR">EUR ()</option>
                  <option value="GBP">GBP ()</option>
                </select>
              </div>
            </div>
          </div>
        </div>
      )}
      
      {/* Vehicle Order Form Modal */}
      {showOrderForm && (
        <VehicleOrderForm
          onSubmit={handleFormSubmit}
          onCancel={handleFormCancel}
          initialData={editingOrder ? {
            model: editingOrder.model,
            year: editingOrder.year,
            country: editingOrder.country,
            supplier: editingOrder.supplier || '',
            vehicleCost: editingOrder.expenses.vehicleCost,
            currency: editingOrder.currency,
            expectedDelivery: editingOrder.expectedDelivery || '',
            notes: editingOrder.notes || '',
            expenses: {
              fuel: editingOrder.expenses.fuel,
              duty: editingOrder.expenses.duty,
              driverCharge: editingOrder.expenses.driverCharge,
              clearanceCharge: editingOrder.expenses.clearanceCharge,
              demurrage: editingOrder.expenses.demurrage,
              tax: editingOrder.expenses.tax,
              cno: editingOrder.expenses.cno,
              cif: editingOrder.expenses.cif,
            },
            paymentMethod: editingOrder.paymentMethod || 'Bank Transfer',
          } : undefined}
          isEditing={!!editingOrder}
        />
      )}

      {/* Order Details Modal */}
      {orderDetailsModal && (
        <div className="vehicle-order-form-overlay">
          <div className="vehicle-order-form order-details-modal">
            <div className="form-header">
              <h2>Order Details - {orderDetailsModal.id}</h2>
              <button className="close-btn" onClick={() => setOrderDetailsModal(null)}></button>
            </div>
            
            <div className="order-details-content">
              <div className="detail-sections">
                {/* Vehicle Information */}
                <div className="detail-section">
                  <h3>Vehicle Information</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <label>Model:</label>
                      <span>{orderDetailsModal.model}</span>
                    </div>
                    <div className="detail-item">
                      <label>Year:</label>
                      <span>{orderDetailsModal.year}</span>
                    </div>
                    <div className="detail-item">
                      <label>Country:</label>
                      <span> {orderDetailsModal.country}</span>
                    </div>
                    <div className="detail-item">
                      <label>Supplier:</label>
                      <span>{orderDetailsModal.supplier || 'N/A'}</span>
                    </div>
                  </div>
                </div>

                {/* Order Information */}
                <div className="detail-section">
                  <h3>Order Information</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <label>Order Date:</label>
                      <span>{orderDetailsModal.orderDate}</span>
                    </div>
                    <div className="detail-item">
                      <label>Expected Delivery:</label>
                      <span>{orderDetailsModal.expectedDelivery || 'TBD'}</span>
                    </div>
                    <div className="detail-item">
                      <label>Status:</label>
                      <span className={`badge ${getStatusBadge(orderDetailsModal.status)}`}>
                        {orderDetailsModal.status.toUpperCase()}
                      </span>
                    </div>
                    <div className="detail-item">
                      <label>Payment Method:</label>
                      <span>{orderDetailsModal.paymentMethod || 'N/A'}</span>
                    </div>
                  </div>
                </div>

                {/* Cost Breakdown */}
                <div className="detail-section">
                  <h3>Cost Breakdown</h3>
                  <div className="expense-breakdown">
                    <div className="expense-item">
                      <span>Vehicle Cost:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.vehicleCost, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item">
                      <span>Fuel:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.fuel, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item">
                      <span>Duty:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.duty, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item">
                      <span>Driver Charge:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.driverCharge, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item">
                      <span>Clearance Charge:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.clearanceCharge, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item">
                      <span>Demurrage:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.demurrage, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item">
                      <span>Tax:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.tax, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item">
                      <span>CNO:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.cno, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item">
                      <span>CIF:</span>
                      <span>{formatCurrency(orderDetailsModal.expenses.cif, orderDetailsModal.currency)}</span>
                    </div>
                    <div className="expense-item total-cost">
                      <span><strong>Total Cost:</strong></span>
                      <span><strong>{formatCurrency(orderDetailsModal.totalCost, orderDetailsModal.currency)}</strong></span>
                    </div>
                    {orderDetailsModal.profit && (
                      <div className="expense-item profit">
                        <span><strong>Profit:</strong></span>
                        <span className="profit-amount">
                          <strong>{formatCurrency(orderDetailsModal.profit, orderDetailsModal.currency)}</strong>
                        </span>
                      </div>
                    )}
                  </div>
                </div>

                {/* Notes */}
                {orderDetailsModal.notes && (
                  <div className="detail-section">
                    <h3>Notes</h3>
                    <div className="notes-content">
                      {orderDetailsModal.notes}
                    </div>
                  </div>
                )}
              </div>

              <div className="detail-actions">
                <button 
                  className="btn btn-secondary" 
                  onClick={() => setOrderDetailsModal(null)}
                >
                  Close
                </button>
                <button 
                  className="btn btn-primary" 
                  onClick={() => {
                    setOrderDetailsModal(null);
                    handleEditOrder(orderDetailsModal);
                  }}
                >
                  Edit Order
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Sale Modal */}
      {saleModal && (
        <div className="vehicle-order-form-overlay">
          <div className="vehicle-order-form sale-modal">
            <div className="form-header">
              <h2> Mark as Sold - {saleModal.model} ({saleModal.year})</h2>
              <button className="close-btn" onClick={() => setSaleModal(null)}></button>
            </div>
            
            <SaleForm
              order={saleModal}
              onSubmit={handleSaleSubmit}
              onCancel={() => setSaleModal(null)}
            />
          </div>
        </div>
      )}
    </div>
  );
};

// Sale Form Component
interface SaleFormProps {
  order: VehicleOrder;
  onSubmit: (data: {
    sellingPrice: number;
    customerName: string;
    customerContact: string;
    customerPaymentMethod: string;
    saleDate: string;
    notes?: string;
  }) => void;
  onCancel: () => void;
}

const SaleForm: React.FC<SaleFormProps> = ({ order, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    sellingPrice: order.marketValue || order.totalCost * 1.2, // Suggest 20% markup
    customerName: '',
    customerContact: '',
    customerPaymentMethod: 'Cash',
    saleDate: new Date().toISOString().split('T')[0],
    notes: ''
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'sellingPrice' ? parseFloat(value) || 0 : value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const calculateProfit = () => formData.sellingPrice - order.totalCost;
  const calculateMargin = () => ((calculateProfit() / formData.sellingPrice) * 100);

  return (
    <div className="sale-form-content">
      <div className="order-summary">
        <h3>Order Summary</h3>
        <div className="summary-grid">
          <div className="summary-item">
            <span>Vehicle:</span>
            <span>{order.model} ({order.year})</span>
          </div>
          <div className="summary-item">
            <span>Order ID:</span>
            <span>{order.id}</span>
          </div>
          <div className="summary-item">
            <span>Total Cost:</span>
            <span>{new Intl.NumberFormat('en-US', {style: 'currency', currency: order.currency}).format(order.totalCost)}</span>
          </div>
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="form-sections">
          <div className="form-section">
            <h3> Sale Information</h3>
            <div className="form-grid">
              <div className="form-group">
                <label htmlFor="sellingPrice" className="form-label">Selling Price *</label>
                <input
                  type="number"
                  id="sellingPrice"
                  name="sellingPrice"
                  value={formData.sellingPrice}
                  onChange={handleChange}
                  className="form-input"
                  required
                  min="0"
                  step="0.01"
                  placeholder="Enter selling price"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="saleDate" className="form-label">Sale Date *</label>
                <input
                  type="date"
                  id="saleDate"
                  name="saleDate"
                  value={formData.saleDate}
                  onChange={handleChange}
                  className="form-input"
                  required
                />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h3> Customer Information</h3>
            <div className="form-grid">
              <div className="form-group">
                <label htmlFor="customerName" className="form-label">Customer Name *</label>
                <input
                  type="text"
                  id="customerName"
                  name="customerName"
                  value={formData.customerName}
                  onChange={handleChange}
                  className="form-input"
                  required
                  placeholder="Customer full name"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="customerContact" className="form-label">Contact Number *</label>
                <input
                  type="text"
                  id="customerContact"
                  name="customerContact"
                  value={formData.customerContact}
                  onChange={handleChange}
                  className="form-input"
                  required
                  placeholder="+971-50-123-4567"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="customerPaymentMethod" className="form-label">Payment Method *</label>
                <select
                  id="customerPaymentMethod"
                  name="customerPaymentMethod"
                  value={formData.customerPaymentMethod}
                  onChange={handleChange}
                  className="form-input"
                  required
                >
                  <option value="Cash">Cash</option>
                  <option value="Bank Transfer">Bank Transfer</option>
                  <option value="Check">Check</option>
                  <option value="Credit Card">Credit Card</option>
                  <option value="Finance">Finance/Loan</option>
                  <option value="Part Exchange">Part Exchange</option>
                </select>
              </div>
            </div>
          </div>

          <div className="form-section">
            <h3> Additional Notes</h3>
            <div className="form-group">
              <label htmlFor="notes" className="form-label">Sale Notes</label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                className="form-input"
                rows={3}
                placeholder="Any additional notes about the sale..."
              />
            </div>
          </div>

          <div className="form-section">
            <div className="profit-summary">
              <h3> Profit Analysis</h3>
              <div className="profit-breakdown">
                <div className="profit-item">
                  <span>Selling Price:</span>
                  <span className="profit-value">
                    {new Intl.NumberFormat('en-US', {style: 'currency', currency: order.currency}).format(formData.sellingPrice)}
                  </span>
                </div>
                <div className="profit-item">
                  <span>Total Cost:</span>
                  <span className="cost-value">
                    -{new Intl.NumberFormat('en-US', {style: 'currency', currency: order.currency}).format(order.totalCost)}
                  </span>
                </div>
                <div className="profit-item total-profit">
                  <span><strong>Net Profit:</strong></span>
                  <span className={`profit-value ${calculateProfit() >= 0 ? 'positive' : 'negative'}`}>
                    <strong>
                      {new Intl.NumberFormat('en-US', {style: 'currency', currency: order.currency}).format(calculateProfit())}
                      {formData.sellingPrice > 0 && (
                        <small> ({calculateMargin().toFixed(1)}%)</small>
                      )}
                    </strong>
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="form-actions">
          <button type="button" className="btn btn-secondary" onClick={onCancel}>
            Cancel
          </button>
          <button type="submit" className="btn btn-success">
             Complete Sale
          </button>
        </div>
      </form>
    </div>
  );
};

export default Dashboard;
