import React, { useState, useEffect } from 'react';
import './App.css';
import Dashboard from './components/Dashboard';
import Header from './components/Header';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [showSplash, setShowSplash] = useState(true);

  // Update browser tab title and handle splash screen
  useEffect(() => {
    document.title = 'Moder Car Sale - Advanced Accounting System';
    
    // Show splash screen for 3 seconds
    const timer = setTimeout(() => {
      setShowSplash(false);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="app">
      <Header activeTab={activeTab} onTabChange={setActiveTab} />
      <main className="main-content">
        <Dashboard activeTab={activeTab} onTabChange={setActiveTab} />
      </main>
      <footer className="app-footer">
        <div className="footer-content">
          <div className="footer-left">
            <p>&copy; 2024 Moder Car Sale. All rights reserved.</p>
          </div>
          <div className="footer-right">
            <p className="developed-by">
              Developed by <span className="company-name">NextWave Tech Labs</span>
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default App;
