import React, { useState, useEffect } from 'react';
import './App.css';
import Dashboard from './components/Dashboard';
import Header from './components/Header';
import Login from './components/Login';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [showSplash, setShowSplash] = useState(true);
  // Initialize from localStorage to persist login on refresh
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    return localStorage.getItem('isLoggedIn') === 'true';
  });

  // Update browser tab title and handle splash screen
  useEffect(() => {
    document.title = 'Moder Car Sale - Advanced Accounting System';

    // Show splash screen for 3 seconds (only if not logged in already)
    const timer = setTimeout(() => {
      setShowSplash(false);
    }, isLoggedIn ? 1000 : 3000); // Shorter splash if already logged in

    return () => clearTimeout(timer);
  }, [isLoggedIn]);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('username');
    setIsLoggedIn(false);
    setActiveTab('overview');
  };

  if (showSplash) {
    return (
      <div className="splash-screen">
        <div className="splash-content">
          <div className="splash-logo">
            <div className="nextwave-logo">
              <span className="sw-text">SW</span>
              <div className="tech-circuits">
                <div className="circuit-line"></div>
                <div className="circuit-line"></div>
                <div className="circuit-line"></div>
              </div>
            </div>
            <h1 className="splash-company-name">NextWave Tech Labs</h1>
            <p className="splash-tagline">Innovative Software Solutions</p>
          </div>
          <div className="splash-loading">
            <div className="loading-bar">
              <div className="loading-progress"></div>
            </div>
            <p>Initializing Moder Car Sale System...</p>
          </div>
        </div>
      </div>
    );
  }

  // Show login if not authenticated
  if (!isLoggedIn) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="app">
      <Header activeTab={activeTab} onTabChange={setActiveTab} onLogout={handleLogout} />
      <main className="main-content">
        <Dashboard activeTab={activeTab} onTabChange={setActiveTab} />
      </main>
      <footer className="app-footer">
        <div className="footer-content">
          <div className="footer-left">
            <p>&copy; 2024 Moder Car Sale. All rights reserved.</p>
          </div>
          <div className="footer-right">
            <p className="developed-by">
              Developed by <span className="company-name">NextWave Tech Labs</span>
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default App;
