import React, { useState } from 'react';
import './Reports.css';

interface Transaction {
  id: string;
  customerId: string;
  type: 'reservation' | 'sale' | 'leasing' | 'refund';
  status: string;
  reservationDate?: string;
  completionDate?: string;
  vehicleDetails: {
    brand: string;
    model: string;
    year: number;
    color: string;
    registrationNo?: string;
  };
  pricing: {
    vehiclePrice: number;
    taxes: number;
    fees: number;
    discount: number;
    totalAmount: number;
  };
  totalPaid: number;
  balanceRemaining: number;
  currency: string;
}

interface Expense {
  id: string;
  category: string;
  description: string;
  amount: number;
  date: string;
  currency: string;
}

interface Customer {
  id: string;
  name: string;
  contact: string;
  email?: string;
  address?: string;
}

interface ReportsProps {
  transactions: Transaction[];
  expenses: Expense[];
}

const Reports: React.FC<ReportsProps> = ({ transactions, expenses }) => {
  const [dateFilter, setDateFilter] = useState<'all' | 'today' | 'week' | 'month' | 'year' | 'custom'>('month');
  const [customDateRange, setCustomDateRange] = useState({ 
    start: new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().split('T')[0],
    end: new Date().toISOString().split('T')[0]
  });
  const [reportType, setReportType] = useState<'sales' | 'expenses' | 'profit' | 'transactions'>('sales');
  const [salesDateFilter, setSalesDateFilter] = useState<'today' | 'week' | 'month' | 'custom'>('month');

  // Helper function to get transaction date
  const getTransactionDate = (transaction: Transaction): string => {
    return transaction.completionDate || transaction.reservationDate || new Date().toISOString();
  };

  // Date filtering logic
  const filterByDate = (date: string) => {
    if (dateFilter === 'all') return true;
    
    const itemDate = new Date(date);
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    if (dateFilter === 'today') {
      const checkDate = new Date(date);
      checkDate.setHours(0, 0, 0, 0);
      return checkDate.getTime() === today.getTime();
    }

    if (dateFilter === 'week') {
      const weekAgo = new Date(today);
      weekAgo.setDate(today.getDate() - 7);
      return itemDate >= weekAgo;
    }

    if (dateFilter === 'month') {
      const monthAgo = new Date(today);
      monthAgo.setMonth(today.getMonth() - 1);
      return itemDate >= monthAgo;
    }

    if (dateFilter === 'year') {
      const yearAgo = new Date(today);
      yearAgo.setFullYear(today.getFullYear() - 1);
      return itemDate >= yearAgo;
    }

    if (dateFilter === 'custom' && customDateRange.start && customDateRange.end) {
      const start = new Date(customDateRange.start);
      const end = new Date(customDateRange.end);
      end.setHours(23, 59, 59, 999);
      return itemDate >= start && itemDate <= end;
    }

    return true;
  };

  // Filter data
  const filteredTransactions = transactions.filter(t => filterByDate(getTransactionDate(t)));
  const filteredExpenses = expenses.filter(e => filterByDate(e.date));

  // Calculate metrics
  const totalRevenue = filteredTransactions
    .filter(t => t.status === 'completed' || t.status === 'fully_paid')
    .reduce((sum, t) => sum + t.pricing.totalAmount, 0);

  const totalExpenses = filteredExpenses.reduce((sum, e) => sum + e.amount, 0);
  const totalProfit = totalRevenue - totalExpenses;
  const totalSales = filteredTransactions.filter(t => t.type === 'sale').length;
  const totalReservations = filteredTransactions.filter(t => t.type === 'reservation').length;
  const totalLeasing = filteredTransactions.filter(t => t.type === 'leasing').length;

  // Sales by vehicle brand
  const salesByBrand = filteredTransactions.reduce((acc, t) => {
    const brand = t.vehicleDetails.brand;
    acc[brand] = (acc[brand] || 0) + t.pricing.totalAmount;
    return acc;
  }, {} as Record<string, number>);

  // Expenses by category
  const expensesByCategory = filteredExpenses.reduce((acc, e) => {
    acc[e.category] = (acc[e.category] || 0) + e.amount;
    return acc;
  }, {} as Record<string, number>);

  // Monthly trends (last 6 months)
  const getMonthlyData = () => {
    const months = [];
    for (let i = 5; i >= 0; i--) {
      const date = new Date();
      date.setMonth(date.getMonth() - i);
      const monthStr = date.toLocaleDateString('en-US', { month: 'short', year: 'numeric' });
      
      const monthTransactions = transactions.filter(t => {
        const tDate = new Date(getTransactionDate(t));
        return tDate.getMonth() === date.getMonth() && tDate.getFullYear() === date.getFullYear();
      });
      
      const monthExpenses = expenses.filter(e => {
        const eDate = new Date(e.date);
        return eDate.getMonth() === date.getMonth() && eDate.getFullYear() === date.getFullYear();
      });

      const revenue = monthTransactions
        .filter(t => t.status === 'completed' || t.status === 'fully_paid')
        .reduce((sum, t) => sum + t.pricing.totalAmount, 0);
      
      const expense = monthExpenses.reduce((sum, e) => sum + e.amount, 0);

      months.push({
        month: monthStr,
        revenue,
        expense,
        profit: revenue - expense
      });
    }
    return months;
  };

  const monthlyData = getMonthlyData();

  const formatCurrency = (amount: number, currency: string = 'LKR') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: 2
    }).format(amount);
  };

  const handleExportPDF = () => {
    alert('ðŸ“„ PDF Export functionality coming soon!\n\nThis will export the current report with all filtered data.');
  };

  const handleExportExcel = () => {
    alert('ðŸ“Š Excel Export functionality coming soon!\n\nThis will export the data to an Excel spreadsheet.');
  };

  return (
    <div className="reports-container">
      {/* Header */}
      <div className="reports-header">
        <div>
          <h2>ðŸ“Š Business Reports & Analytics</h2>
          <p className="subtitle">Comprehensive financial insights and performance metrics</p>
        </div>
        <div className="export-buttons">
          <button className="btn btn-secondary" onClick={handleExportPDF}>
            ðŸ“„ Export PDF
          </button>
          <button className="btn btn-success" onClick={handleExportExcel}>
            ðŸ“Š Export Excel
          </button>
        </div>
      </div>

      {/* Filters */}
      <div className="reports-filters">
        <div className="filter-group">
          <label>Report Type:</label>
          <select
            className="filter-select"
            value={reportType}
            onChange={(e) => setReportType(e.target.value as any)}
          >
            <option value="sales">Sales Report</option>
            <option value="expenses">Expenses Report</option>
            <option value="profit">Profit & Loss</option>
            <option value="transactions">Transaction Summary</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Date Range:</label>
          <select
            className="filter-select"
            value={dateFilter}
            onChange={(e) => setDateFilter(e.target.value as any)}
          >
            <option value="all">All Time</option>
            <option value="today">Today</option>
            <option value="week">Last 7 Days</option>
            <option value="month">Last 30 Days</option>
            <option value="year">Last Year</option>
            <option value="custom">Custom Range</option>
          </select>
        </div>

        {dateFilter === 'custom' && (
          <>
            <div className="filter-group">
              <label>From:</label>
              <input
                type="date"
                className="date-input"
                value={customDateRange.start}
                onChange={(e) => setCustomDateRange({...customDateRange, start: e.target.value})}
              />
            </div>
            <div className="filter-group">
              <label>To:</label>
              <input
                type="date"
                className="date-input"
                value={customDateRange.end}
                onChange={(e) => setCustomDateRange({...customDateRange, end: e.target.value})}
              />
            </div>
          </>
        )}
      </div>

      {/* Summary Cards */}
      <div className="summary-cards-grid">
        <div className="summary-card revenue">
          <div className="card-icon">ðŸ’°</div>
          <div className="card-content">
            <h3>{formatCurrency(totalRevenue)}</h3>
            <p>Total Revenue</p>
          </div>
        </div>

        <div className="summary-card expenses">
          <div className="card-icon">ðŸ’¸</div>
          <div className="card-content">
            <h3>{formatCurrency(totalExpenses)}</h3>
            <p>Total Expenses</p>
          </div>
        </div>

        <div className="summary-card profit">
          <div className="card-icon">ðŸ“ˆ</div>
          <div className="card-content">
            <h3 className={totalProfit >= 0 ? 'positive' : 'negative'}>
              {formatCurrency(totalProfit)}
            </h3>
            <p>Net Profit</p>
          </div>
        </div>

        <div className="summary-card transactions">
          <div className="card-icon">ðŸš—</div>
          <div className="card-content">
            <h3>{totalSales + totalReservations + totalLeasing}</h3>
            <p>Total Transactions</p>
          </div>
        </div>
      </div>

      {/* Report Content */}
      {reportType === 'sales' && (
        <div className="report-section">
          <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px'}}>
            <h3>Sales Report</h3>
            
            {/* Date Filter Buttons */}
            <div className="date-filter-buttons">
              <button 
                className={`filter-btn ${salesDateFilter === 'today' ? 'active' : ''}`}
                onClick={() => setSalesDateFilter('today')}
              >
                ðŸ“… Today
              </button>
              <button 
                className={`filter-btn ${salesDateFilter === 'week' ? 'active' : ''}`}
                onClick={() => setSalesDateFilter('week')}
              >
                ðŸ“Š This Week
              </button>
              <button 
                className={`filter-btn ${salesDateFilter === 'month' ? 'active' : ''}`}
                onClick={() => setSalesDateFilter('month')}
              >
                ðŸ“† This Month
              </button>
              <button 
                className={`filter-btn ${salesDateFilter === 'custom' ? 'active' : ''}`}
                onClick={() => setSalesDateFilter('custom')}
              >
                ðŸ—“ï¸ Custom Date
              </button>
            </div>
          </div>

          {/* Custom Date Range Inputs */}
          {salesDateFilter === 'custom' && (
            <div className="card" style={{marginBottom: '20px', padding: '15px'}}>
              <div style={{display: 'flex', gap: '15px', alignItems: 'center'}}>
                <div>
                  <label style={{marginRight: '10px', fontWeight: 600}}>From:</label>
                  <input
                    type="date"
                    className="date-input"
                    value={customDateRange.start}
                    onChange={(e) => setCustomDateRange({...customDateRange, start: e.target.value})}
                  />
                </div>
                <div>
                  <label style={{marginRight: '10px', fontWeight: 600}}>To:</label>
                  <input
                    type="date"
                    className="date-input"
                    value={customDateRange.end}
                    onChange={(e) => setCustomDateRange({...customDateRange, end: e.target.value})}
                  />
                </div>
              </div>
            </div>
          )}

          {/* Sales Data based on selected filter */}
          {(() => {
            // Filter sales based on selected date filter
            let salesData = transactions.filter(t => t.status === 'completed' || t.status === 'fully_paid');
            const today = new Date();
            today.setHours(0, 0, 0, 0);

            if (salesDateFilter === 'today') {
              salesData = salesData.filter(t => {
                const tDate = new Date(getTransactionDate(t));
                tDate.setHours(0, 0, 0, 0);
                return tDate.getTime() === today.getTime();
              });
            } else if (salesDateFilter === 'week') {
              const weekAgo = new Date(today);
              weekAgo.setDate(today.getDate() - 7);
              salesData = salesData.filter(t => {
                const tDate = new Date(getTransactionDate(t));
                return tDate >= weekAgo;
              });
            } else if (salesDateFilter === 'month') {
              const monthAgo = new Date(today);
              monthAgo.setMonth(today.getMonth() - 1);
              salesData = salesData.filter(t => {
                const tDate = new Date(getTransactionDate(t));
                return tDate >= monthAgo;
              });
            } else if (salesDateFilter === 'custom') {
              salesData = salesData.filter(t => {
                const tDate = new Date(getTransactionDate(t));
                const start = new Date(customDateRange.start);
                const end = new Date(customDateRange.end);
                end.setHours(23, 59, 59, 999);
                return tDate >= start && tDate <= end;
              });
            }

            const totalSalesCount = salesData.length;
            const totalSalesRevenue = salesData.reduce((sum, t) => sum + t.pricing.totalAmount, 0);
            const avgPerSale = totalSalesCount > 0 ? totalSalesRevenue / totalSalesCount : 0;

            return (
              <>
                {/* Summary Cards */}
                <div className="grid grid-3" style={{marginBottom: '30px'}}>
                  <div className="summary-card revenue">
                    <div className="card-icon">ðŸš—</div>
                    <div className="card-content">
                      <h3>{totalSalesCount}</h3>
                      <p>Total Vehicles Sold</p>
                    </div>
                  </div>

                  <div className="summary-card profit">
                    <div className="card-icon">ðŸ’°</div>
                    <div className="card-content">
                      <h3 className="positive">{formatCurrency(totalSalesRevenue)}</h3>
                      <p>Total Revenue</p>
                    </div>
                  </div>

                  <div className="summary-card transactions">
                    <div className="card-icon">ðŸ“Š</div>
                    <div className="card-content">
                      <h3>{formatCurrency(avgPerSale)}</h3>
                      <p>Average per Sale</p>
                    </div>
                  </div>
                </div>

                {/* Detailed Sales Table */}
                <div className="card" style={{marginBottom: '20px'}}>
                  <h4>Sales Details</h4>
                  <table className="report-table">
                    <thead>
                      <tr>
                        <th>Date</th>
                        <th>Transaction ID</th>
                        <th>Customer</th>
                        <th>Vehicle</th>
                        <th>Type</th>
                        <th className="text-right">Amount</th>
                      </tr>
                    </thead>
                    <tbody>
                      {salesData.length > 0 ? (
                        salesData.map(transaction => {
                          const customer = customers.find(c => c.id === transaction.customerId);
                          return (
                            <tr key={transaction.id}>
                              <td>{new Date(getTransactionDate(transaction)).toLocaleDateString()}</td>
                              <td><strong>{transaction.id}</strong></td>
                              <td>{customer?.name || 'N/A'}</td>
                              <td>{transaction.vehicleDetails.brand} {transaction.vehicleDetails.model}</td>
                              <td><span className="badge">{transaction.type}</span></td>
                              <td className="text-right positive"><strong>{formatCurrency(transaction.pricing.totalAmount, transaction.currency)}</strong></td>
                            </tr>
                          );
                        })
                      ) : (
                        <tr>
                          <td colSpan={6} className="no-data">No sales found for selected period</td>
                        </tr>
                      )}
                    </tbody>
                    {salesData.length > 0 && (
                      <tfoot>
                        <tr className="total">
                          <td colSpan={5}><strong>TOTAL</strong></td>
                          <td className="text-right positive"><strong>{formatCurrency(totalSalesRevenue)}</strong></td>
                        </tr>
                      </tfoot>
                    )}
                  </table>
                </div>

                {/* Additional Analysis */}
                <div className="grid grid-2">
                  <div className="card">
                    <h4>Sales by Type</h4>
                    <div className="stat-list">
                      <div className="stat-item">
                        <span>Direct Sales:</span>
                        <strong>{salesData.filter(t => t.type === 'sale').length} vehicles</strong>
                      </div>
                      <div className="stat-item">
                        <span>Reservations:</span>
                        <strong>{salesData.filter(t => t.type === 'reservation').length} vehicles</strong>
                      </div>
                      <div className="stat-item">
                        <span>Leasing:</span>
                        <strong>{salesData.filter(t => t.type === 'leasing').length} vehicles</strong>
                      </div>
                    </div>
                  </div>

                  <div className="card">
                    <h4>Top Selling Brands</h4>
                    <div className="stat-list">
                      {(() => {
                        const brandSales = salesData.reduce((acc, t) => {
                          const brand = t.vehicleDetails.brand;
                          acc[brand] = (acc[brand] || 0) + t.pricing.totalAmount;
                          return acc;
                        }, {} as Record<string, number>);

                        return Object.entries(brandSales).length > 0 ? (
                          Object.entries(brandSales)
                            .sort(([, a], [, b]) => b - a)
                            .slice(0, 5)
                            .map(([brand, amount]) => (
                              <div key={brand} className="stat-item">
                                <span>{brand}:</span>
                                <strong>{formatCurrency(amount)}</strong>
                              </div>
                            ))
                        ) : (
                          <p className="no-data" style={{textAlign: 'center', padding: '20px'}}>No data</p>
                        );
                      })()}
                    </div>
                  </div>
                </div>
              </>
            );
          })()}
        </div>
      )}

      {reportType === 'expenses' && (
        <div className="report-section">
          <h3>Expense Analysis</h3>
          
          <div className="card">
            <h4>Expenses by Category</h4>
            <div className="expense-breakdown">
              {Object.entries(expensesByCategory)
                .sort(([, a], [, b]) => b - a)
                .map(([category, amount]) => (
                  <div key={category} className="expense-bar">
                    <div className="expense-label">
                      <span>{category}</span>
                      <strong>{formatCurrency(amount)}</strong>
                    </div>
                    <div className="progress-bar">
                      <div 
                        className="progress-fill"
                        style={{width: `${(amount / totalExpenses) * 100}%`}}
                      />
                    </div>
                    <span className="percentage">
                      {((amount / totalExpenses) * 100).toFixed(1)}%
                    </span>
                  </div>
                ))}
            </div>
          </div>
        </div>
      )}

      {reportType === 'profit' && (
        <div className="report-section">
          <h3>Profit & Loss Statement</h3>
          
          <div className="card">
            <table className="report-table">
              <thead>
                <tr>
                  <th>Category</th>
                  <th className="text-right">Amount</th>
                </tr>
              </thead>
              <tbody>
                <tr className="section-header">
                  <td colSpan={2}><strong>Revenue</strong></td>
                </tr>
                <tr>
                  <td>Total Sales Revenue</td>
                  <td className="text-right">{formatCurrency(totalRevenue)}</td>
                </tr>
                <tr className="section-header">
                  <td colSpan={2}><strong>Expenses</strong></td>
                </tr>
                {Object.entries(expensesByCategory).map(([category, amount]) => (
                  <tr key={category}>
                    <td className="indent">{category}</td>
                    <td className="text-right negative">({formatCurrency(amount)})</td>
                  </tr>
                ))}
                <tr className="subtotal">
                  <td><strong>Total Expenses</strong></td>
                  <td className="text-right negative"><strong>({formatCurrency(totalExpenses)})</strong></td>
                </tr>
                <tr className="total">
                  <td><strong>Net Profit</strong></td>
                  <td className={`text-right ${totalProfit >= 0 ? 'positive' : 'negative'}`}>
                    <strong>{formatCurrency(totalProfit)}</strong>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      )}

      {reportType === 'transactions' && (
        <div className="report-section">
          <h3>Transaction Summary</h3>
          
          <div className="card">
            <table className="report-table">
              <thead>
                <tr>
                  <th>Transaction ID</th>
                  <th>Date</th>
                  <th>Type</th>
                  <th>Vehicle</th>
                  <th>Status</th>
                  <th className="text-right">Amount</th>
                </tr>
              </thead>
              <tbody>
                {filteredTransactions.length > 0 ? (
                  filteredTransactions.map(transaction => (
                    <tr key={transaction.id}>
                      <td>{transaction.id}</td>
                      <td>{new Date(getTransactionDate(transaction)).toLocaleDateString()}</td>
                      <td><span className="badge">{transaction.type}</span></td>
                      <td>{transaction.vehicleDetails.brand} {transaction.vehicleDetails.model}</td>
                      <td><span className={`status-badge ${transaction.status}`}>{transaction.status}</span></td>
                      <td className="text-right"><strong>{formatCurrency(transaction.pricing.totalAmount, transaction.currency)}</strong></td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan={6} className="no-data">No transactions found for selected period</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Monthly Trends */}
      <div className="report-section">
        <h3>Monthly Performance Trends</h3>
        <div className="card">
          <table className="report-table">
            <thead>
              <tr>
                <th>Month</th>
                <th className="text-right">Revenue</th>
                <th className="text-right">Expenses</th>
                <th className="text-right">Profit</th>
              </tr>
            </thead>
            <tbody>
              {monthlyData.map(month => (
                <tr key={month.month}>
                  <td><strong>{month.month}</strong></td>
                  <td className="text-right positive">{formatCurrency(month.revenue)}</td>
                  <td className="text-right negative">({formatCurrency(month.expense)})</td>
                  <td className={`text-right ${month.profit >= 0 ? 'positive' : 'negative'}`}>
                    <strong>{formatCurrency(month.profit)}</strong>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Reports;
